INSERT INTO `nge_icl_string_translations` VALUES('132', '6749', 'kh', '10', 'នៅពេលដែលនិយាយពីសហគ្រិនក្មេងខ្ចី សំណួរមួយដែលយើងទទួលបានច្រើនជាងគេគឺ “តើខ្ញុំអាចចាប់ផ្តើមអាជីវកម្មដោយដូចម្តេចប្រសិនបើខ្ញុំមិនមានប្រាក់”។ ដោយអានចំនុចខ្លឺៗខាងក្រោមនេះ អ្នកនឹងដឹងពីវិធីសាមញ្ញៗបំផុតដើម្បីទទួលបានដើមទុនក្នុងការចាប់ផ្តើមអាជីវកម្មថ្មីមួយ។', NULL, '1', 'local', '0', '2019-07-26 02:02:03');
INSERT INTO `nge_icl_string_translations` VALUES('133', '6750', 'kh', '10', '<strong><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ប្រើប្រាស់ធនធានផ្ទាល់ខ្លួនរបស់អ្នក</span></strong>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">វាមិនចាំបាច់ក្នុងការមានដើមទុនច្រើនដើម្បីចាប់ផ្តើមអាជីវកម្មនោះទេ ហើយជាទូទៅ</span><span lang=\"KHM\">​</span><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">អាជីវកម្មក្នុងសុបិន្តរបស់អ្នកអាចកើតមានឡើងដំបូងដោយមានទំហំតូច ដូចនេះអ្នកអាចចាប់ផ្តើមជាមួយនឹងប្រាក់ដ៏តិចតួច ហើយពង្រីកទំហំអាជីវកម្មលោកអ្នកតាមដំណាក់កាល។ វាជាមធ្យោបាយដ៏ល្អមួយដើម្បីសាកល្បងគោលដៅទីផ្សារ និងគុណតម្លៃផលិតផលរបស់អ្នក។ សូមគិតពីវិធីសាស្ត្រផ្សេងៗដែលអ្នកអាចសន្សំប្រាក់ដើម្បីបង្កើតអាជីវកម្មតូចមួយ ហើយប្រើប្រាស់ប្រាក់ចំណេញទាំងនោះធ្វើជាប្រាក់សន្សំដើម្បីពង្រីកអាជីវកម្មឲ្យកាន់តែធំជាងមុន។ ក្នុងដំណាក់ដំបូងនៃការចាប់ផ្តើមអាជីវកម្ម ភាគច្រើន ស្របពេលដែលសហគ្រិនកំពុងគ្រប់គ្រងពន្លកអាជី</span><span lang=\"KHM\">​</span><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">វកម្ម គេអាចនឹងឆ្លៀតពេលធ្វើការងារខាងក្រៅផងដែរ រហូតដល់ពួកគេមានទុនគ្រប់គ្រាន់ដើម្បីធ្វើអាជីវកម្មបាន១០០%។</span>\r\n\r\n<span style=\"font-family: \'Khmer OS\';\"> </span>\r\n\r\n<strong><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">គ្រួសារ និង មិត្តភក្តិ</span></strong>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ការស្នើរសុំប្រាក់ពីគ្រួសារ និង មិត្តភក្តិជាមធ្យោបាយដ៏ល្អ ហើយសាមញ្ញបំផុតមួយដែលអនុញ្ញាតឲ្យសហគ្រិនអាចចាប់ផ្តើមអាជីវកម្មបាន។ ជាថ្មីម្តងទៀត វាជារឿងល្អដែលអ្នកអាចចាប់ផ្តើមអាជីវកម្មរបស់អ្នកជាអាជីវកម្មខ្នាតតូចជាមុនសិន មុនពេលអ្នកអាចមានប្រាក់គ្រប់គ្រាន់ដើម្បីពង្រីកទំហំអាជីវកម្ម។ </span>\r\n\r\n<span style=\"font-family: \'Khmer OS\';\"> </span>\r\n\r\n<strong><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ជំនួយឥតសំណង</span></strong>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">មានជំនួយឥតសំណងមួយចំនួននៅប្រទេសកម្ពុជាដែលអាចផ្តល់ជាទុនសំរាប់អាជីវកម្មខ្នាតតូច។ ប្រសិនបើអ្នកមានគំនិតអាជីវកម្មដែលមានលក្ខណៈពិសេស យើងលើកទឹកចិត្តឲ្យអ្នកធ្វើការស្រាវជ្រាវពីជំនួយ ក៏ដូចជាលក្ខខណ្ឌសមស្របផ្សេងៗ មុនពេលដាក់ពាក្យស្នើសុំ។</span>\r\n\r\n<span style=\"font-family: \'Khmer OS\';\"> </span>\r\n\r\n<strong><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ឥណទានខ្នាតតូច</span></strong>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ឥណទានខ្នាតតូចជាប្រាក់កម្ចីដែលងាយទទួលបានបំផុតក្នុងប្រទេសកម្ពុជា ហើយវាអាចជាវិធីដ៏ល្អក្នុងការចាប់ផ្តើមអាជីវកម្ម។ អ្នកត្រូវតែស្វែងយល់ពីចំណាយដែលពាក់ព័ន្ធអោយបាន</span><span lang=\"KHM\">​</span><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ច្បាស់លាស់ ហើយពិនិត្យមើលលក្ខខណ្ឌចាំបាច់ផ្សេងៗទៀតនៃកម្ចី មុនពេលអ្នកសម្រេចចិត្តស្នើរសុំឥណទានខ្នាតតូច។ សូមមើលពីរបៀបគណនារបស់យើង ដើម្បីជួយអ្នកអោយឃើញពីការប្រាក់ដែលអ្នកត្រូវបង់។</span>\r\n\r\n<span style=\"font-family: \'Khmer OS\';\"> </span>\r\n\r\n<strong><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">ការវិនិយោគ និង ប្រាក់</span></strong><b><span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">កម្ចី</span></b>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">វាប្រាកដជាមានការលំបាកក្នុងការទទួលបានការវិនិយោគ និង ប្រាក់កម្ចីទំហំធំចំពោះអាជីវកម្មដែលមិនទាន់មានរូបរាង។ ផ្ទុយមកវិញ វាក៏អាចកើតមានក្នុងករណីមួយចំនួនផងដែរ។ ប្រសិនបើអ្នកចង់ពិភាក្សាបន្ថែមស្តីពីការវិនិយោគ សូមទាក់ទងមកពួកយើង តាមរយៈគេហទំព័រ </span><a href=\"http://ngeayngeay.co\"><span style=\"font-family: \'Khmer OS\';\">ngeayngeay.com</span></a><span style=\"font-family: \'Khmer OS\';\"> <span lang=\"KHM\">នោះយើងនឹងអាចជួយអ្នក ដើម្បីបង្កើតទំនាក់ទំនងដោយផ្ទាល់ជាមួយអ្នកវិនិយោគ ។</span></span>\r\n\r\n<span lang=\"KHM\" style=\"font-family: \'Khmer OS\';\">សូមចូលមើលគេហទំព័រ </span><span style=\"font-family: \'Khmer OS\';\">Funding Opportunities <span lang=\"KHM\">ដើម្បីទទួលបានព័ត៌មានពីឱកាសផ្សេងៗសម្រាប់អាជីវកម្មរបស់លោកអ្នក</span></span>', NULL, '1', 'local', '0', '2019-07-26 02:02:03');
