INSERT INTO `nge_icl_string_translations` VALUES('167', '6559', 'kh', '10', 'នៅពេលអ្នកចុះបញ្ជីពាណិជ្ជកម្ម តម្រូវការដែលចំាបាច់បំផុតមួយគឺគណនេយ្យដែលច្បាស់លាស់ហើយសមរម្យ។', NULL, '1', 'local', '0', '2019-07-26 05:34:51');
INSERT INTO `nge_icl_string_translations` VALUES('168', '6560', 'kh', '10', 'អ្នកជាប់ពន្ធខ្នាតតូច(កម្មសិទ្ធតែមួយ ឬ ជាដៃគូរ)\r\n\r\nតម្រូវការចំពោះអ្នកជាប់ពន្ធខ្នាតតូចគឺមានសារសំខាន់តិចជាងអ្នកជាប់ពន្ធខ្នាតមធ្យម និង ធំ។ អ្នកជាប់ពន្ធខ្នាតតូចតម្រូវអោយមានសៀវភៅ៣ក្បាល:\r\n<ul>\r\n 	<li>សៀវភៅកត់ត្រាការទិញប្រចាំថ្ងៃ</li>\r\n</ul>\r\nនេះជាការប្រមូលផ្ថុំរាល់ប្រតិបត្តិការចំណាយទាំងអស់របស់ក្រុមហ៊ុន គឺសំដៅលើការទិញ។ វាអាចជាៈ ទំនិញ ឬសេវ៉ាកម្ម វត្តុធាតុដើម ប្រាក់ឈ្នូលនិយោជិក វិក័យប័ត្រប្រើប្រាស់.... ការកត់ត្រានេះត្រូវធ្វើជាប្រចាំថ្ងៃ។\r\n<ul>\r\n 	<li>សៀវភៅកត់ត្រាការលក់ប្រចាំថ្ងៃ</li>\r\n</ul>\r\nសៀវភៅលក់ប្រចាំថ្ងៃ អាចជាកំណត់ត្រាប្រចាំថ្ងៃ ដែលសំដៅទៅលើទំនិញនឹងសេវ៉ាកម្មទាំងអស់ ដែលបានលក់ដូរ ដោយក្រុមហ៊ុន។\r\n<ul>\r\n 	<li>សៀវភៅកត់ត្រាសន្និធិ</li>\r\n</ul>\r\nសៀវភៅកត់ត្រាសន្និធិ គឺសំដៅលើការកត់ត្រាលើអ្វីដែលក្រុមហ៊ុនមាននៅក្នុងស្តុក។\r\n\r\nការស្នើរសុំដ៏ទៃទៀត តែមិនមែនជាតម្រូវការផ្នែកច្បាប់រួមមានៈ\r\n\r\nគណនីយត្រូវទទួល\r\n\r\nនេះជាប្រាក់ដែលគេជំពាក់អ្នក\r\n\r\nគណនីយត្រូវបង់\r\n\r\nនេះជាប្រាក់ដែលអ្នកជំពាក់គេ\r\n\r\nសៀវភៅទ្រព្យសម្បិត្តិ\r\n\r\nរាល់សំភារះ គ្រឿងសង្ហារឹម ឬគ្រឿងចក្រដែលអ្នកទិញដែលមានតំលៃលើស១០០ដុល្លា\r\n\r\nអ្នកជាប់ពន្ធខ្នាតមធ្យម និង ធំ​\r\n\r\nក្នុងនាមជាអ្នកជាប់ពន្ធដែលជាតម្រូវការសំរាប់អ្នកជាប់ពន្ធខ្នាតមធ្យម និងធំគឺមានភាពចំាបាច់ហើយស្មុគស្មាញច្រើនជាងអ្នកជាប់ពន្ធខ្នាតតូច ដែលយើងសូមផ្ថល់យោបល់អោយប្រើប្រាស់និតិវិធីគណនេយ្យផ្លូវការដូចជា CIFRS ហើយប្រើប្រាស់ប្រពន្ធ័ដូចជា quickbooks, xero ឬ banhji។\r\n\r\nប្រសិនបើអ្នកជាអ្នកថ្មីក្នុងការ កត់ត្រាសៀវភៅ ហើយធ្វើគណនេយ្យ យើងសូមណែនាំអោយអ្នកជួលអ្នកដែលមានវិជ្ជាជីវះដើម្បីជួយអ្នកលើការងារទាំងនេះ។ សូមមើលនៅទីនេះ។', NULL, '1', 'local', '0', '2019-07-26 05:34:51');
