INSERT INTO `nge_icl_string_translations` VALUES('203', '6538', 'kh', '10', 'មានការបង់ប្រាក់២បែបចំពោះបេឡាជាតិរបបសន្តិសុខសង្គម ដែលអាចធ្វើបានតាមខែនីមួយៗដោយនយោជក់។', NULL, '1', 'local', '0', '2019-07-26 05:39:17');
INSERT INTO `nge_icl_string_translations` VALUES('204', '6539', 'kh', '10', 'គម្រោងហានិភ័យមុខងារ\r\n\r\nអត្រាសំរាប់ហានិភ័យមុខងាគឺមានតំលៃ០,៨% នៃប្រាក់ខែសរុបកម្មករ។\r\n\r\nគម្រោងថែរក្សាសុខភាព\r\n\r\nគម្រោងថែរក្សាសុខភាពគឺមានតំលៃ២,៦%នៃប្រាក់ខែសរុបកម្មករ។\r\n\r\nកម្មករទាំងអស់ត្រូវបានចុះឈ្មោះជាមួយរបបជាតិសន្តិសុខសង្គមក្នុងរយៈពេល៣ថ្ងៃចាប់ពីថ្ងៃចាប់ផ្តើមធ្វើការងារ។​\r\n\r\nកាលកំណត់បង់ប្រាក់បេឡាជាតិរបបសន្តិសុខសង្គមៈ ថ្ងៃទី១៥ នៃខែនីមួយៗ', NULL, '1', 'local', '0', '2019-07-26 05:39:17');
INSERT INTO `nge_icl_string_translations` VALUES('205', '6531', 'kh', '10', 'ការចុះបញ្ជីក្នុងក្រសួងការងារ', NULL, '1', 'local', '0', '2019-07-26 05:40:42');
INSERT INTO `nge_icl_string_translations` VALUES('206', '6532', 'kh', '10', 'រាល់អាជីវកម្មដែលបានចុះបញ្ជីជាមួយក្រសួងពាណិជ្ជកម្ម និង អគ្គនាយកដ្ឋានពន្ធដារត្រូវតែចុះបញ្ជីជាមួយក្រសួងការងារ។', NULL, '1', 'local', '0', '2019-07-26 05:40:43');
INSERT INTO `nge_icl_string_translations` VALUES('207', '6533', 'kh', '10', 'ដើម្បីចុះបញ្ជីក្រសួងការងារអ្នកត្រូវតែមាន\r\n<ul>\r\n 	<li>ពន្ធប៉ាតង់</li>\r\n 	<li>វិញ្ញាបនបត្រ័ការចុះបញ្ជីក្រុមហ៊ុន</li>\r\n 	<li>បញ្ជីនយោជិក (សូមមើលទំរង់នៅទីនេះ)</li>\r\n 	<li>បំពេញបែបបទក្រសួងការងារ​</li>\r\n</ul>\r\nយកឯកសារទាំងអស់ទៅកាន់អគារដែលមានឈ្មោះ ថា អគ្គការងារ ជាន់ទី៣។ អគារអគ្គការងារស្តិតនៅខាងក្រោយ ផ្នែកខាងឆ្វេង', NULL, '1', 'local', '0', '2019-07-26 05:40:43');
