INSERT INTO `nge_icl_string_translations` VALUES('212', '6521', 'kh', '10', 'To be able to import/export it is important that you have applied for this in your tax patent with the General Department of Tax', NULL, '1', 'local', '0', '2019-07-26 05:42:50');
INSERT INTO `nge_icl_string_translations` VALUES('213', '6522', 'kh', '10', 'For importing and exporting, you need to register your patent as an import/export patent when you register with the General Department of Tax.\r\n\r\nWhen you are ready to do your first import/export you need to register with the Automated System for Customs Data (ASYCUDA). To do this you will need to go to the customs office located at No. 6-8, Preah Norodom Blvd., Sangkat Phsar Thmei III, Khan Daun Penh, Phnom Penh, Cambodia.\r\n\r\nIf you are importing/exporting any items deemed sensitive by the government then you will need to obtain a licence from the relevant ministry.', NULL, '1', 'local', '0', '2019-07-26 05:42:50');
