INSERT INTO `nge_icl_string_translations` VALUES('216', '6517', 'kh', '10', 'ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ជាប្រព័ន្ធថ្មី១ដែលបានអនុវត្តក្នុងប្រទេសកម្ពុជាក្នុងឆ្នាំ២០១៩ ដើម្បីជួយរដ្ឋាភិបាលក្នុងការតាមដានការបង់ពន្ធតំលៃបន្តែមអោយមានលក្ខណះល្អប្រសើរ។ ដើម្បីបញ្ចូលព័ត៌មានរបស់អ្នកសូមចូលទៅកាន់គេហទំព័រខាងក្រោម។\r\n\r\n<a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">https://owp.tax.gov.kh:50005/epaymentowpweb/login</a>\r\n<ul>\r\n 	<li>អាជីវកម្មនីមួយៗនឹងត្រូវបង្កើតឈ្មោះអ្នកប្រើប្រាស់ ហើយបំពេញព័ត៌មានរបស់ក្រុមហ៊ុន។</li>\r\n 	<li>អាជីវកម្មនីមួយៗត្រូវតែបញ្ជូលការទិញដូរដែលពួកគេត្រូវបានគិតប្រាក់ថ្លៃពន្ធតំលៃបន្ថែម</li>\r\n 	<li>អាជីវកម្មទាំងអស់ត្រូវតែបញ្ចូលការលក់ទាំងអស់ដែលពួកគេបានធ្វើជារៀងរាល់ខែ</li>\r\n</ul>\r\nកាលបរិច្ឆេតផុតកំណត់ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ៖ ថ្ងៃទី២០ រៀងរាល់ខែនីមួយៗ។\r\n\r\nអ្នកត្រូវតែភ្ជាប់ឯកសារប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ទៅលើពន្ធប្រាក់ចំណេញប្រចាំខែរបស់អ្នក។', NULL, '1', 'local', '0', '2019-07-26 05:44:29');
