INSERT INTO `nge_icl_string_translations` VALUES('391', '6020', 'kh', '10', 'អ្នកនឹងត្រូវយកឯកសារចាំបាច់ទាំងអស់ដើម្បីធ្វើការចុះបញ្ជីសារពើពន្ធ។ ដំណើរការចុះបញ្ជីធ្វើឡើងនៅអគារសាសាអគ្គនាយកដ្ឋានពន្ធដារ។ ការបង់ប្រាក់ពន្ធ ត្រូវតែធ្វើនៅសាខាពន្ធដា នៅការិយាល័យពន្ធដាណាមួយនៅក្នុងក្រុង។\r\n\r\nនៅពេលដែលអ្នកបានចុះបញ្ជីហើយ អ្នកនឹងទទួលបាន:\r\n<ul>\r\n 	<li>លេខអត្តសញ្ញាណកម្មសារពើពន្ធ(TIN): នេះជាលេខដែលបញ្ជាក់ថាអ្នកជាអ្នកបង់ពន្ធជាមួយ​សមត្តិកិច្ច</li>\r\n 	<li>ប៉ាតង់បង់ពន្ធ</li>\r\n 	<li>ពន្ធអាការលើតម្លៃបន្ថែម</li>\r\n</ul>\r\n<strong>តំលៃនៃការចុះបញ្ជី៖ </strong>\r\n\r\n១០០ ដុល្លាចំពោះការចុះបញ្ជី ហើយ ៣០០ដុល្លាចំពោះពន្ធប៉ាតង់\r\n\r\n&nbsp;', NULL, '1', 'local', '0', '2019-07-26 06:18:34');
