INSERT INTO `nge_icl_string_translations` VALUES('462', '5775', 'kh', '10', '<p>On the left side click \"reserve a company name\"</p><p>Write your preferred name in both Khmer and English</p><p>The supporting documents are not compulsory</p><p>Submit when you are ready, you will be redirected to the payment page</p><p>If you have an ACLEDA account, click ACLEDA</p><p>If you do not have an ACLEDA account click cancel, then click pay later. You will have to go to ACLEDA in person and pay. </p>', NULL, '1', 'local', '0', '2019-07-26 06:34:35');
