INSERT INTO `nge_icl_string_translations` VALUES('464', '5777', 'kh', '10', '<p>Once your name is approved you can register online. The online form requires you to upload the Memorandum and Articles of Association as well as other documents, click here for more information</p>', NULL, '1', 'local', '0', '2019-07-26 06:34:35');
