INSERT INTO `nge_icl_strings` VALUES('4918', 'en', 'page-builder-shortcode-strings-60', 'ef5c796277027f9b7351f1f9aecec192', 'Welcome to Ngeay Ngeay', '2', '1', '', 'LINE', 'ultimate_heading: main_heading', '0', '', '5ddacb4d26a327ba70f80bb6697fd7dc', 'optional', '4');
INSERT INTO `nge_icl_strings` VALUES('4923', 'en', 'page-builder-shortcode-strings-60', 'd18d910af416586cda9c74b95b9e8abe', '<span style=\"font-size: 16px; font-family: Poppins; font-weight: 500;\">Ngeay Ngeay (Easy Easy) aims to make business information accessible for all Cambodians, particularly female business owners. Our aim is that any Cambodian business owner can easily access all the information needed to register their business.</span>\r\n\r\n<span style=\"font-size: 16px; font-family: Poppins;\">In Cambodia we know it can be difficult access to information regarding business registration and tax responsibilities, this website aims to provide all this info for free in a clear and detailed way. We cover every step of business registration from what documents you need to register to what are your responsibilities following registration. We hope to be able to help you make an informed decision about business and tax registration in Cambodia.</span>\r\n\r\n<span style=\"font-size: 16px; font-family: Poppins;\">This project is an initiative of SHE Investments, a social enterprise which supports Cambodian female entrepreneurs to scale their business, and it has been made possible thanks to the support of USAID, Development Innovations and Ezecom.</span>', '2', '2', '', 'VISUAL', 'vc_column_text: content', '0', '', 'be41217c1f2b92fdc7f94ad6cc5b5cf9', 'optional', '145');
INSERT INTO `nge_icl_strings` VALUES('4924', 'en', 'page-builder-shortcode-strings-60', 'fd6df34ee4e7642c02ced6012028f6b8', 'Partners', '2', '3', '', 'LINE', 'ultimate_heading: main_heading', '0', '', '26ff433ee981be55fd2f67a9f32e34f0', 'optional', '1');
INSERT INTO `nge_icl_strings` VALUES('4925', 'en', 'page-builder-shortcode-strings-60', '75a21d7ff6c7f5fc6e1b3b4e23e3131e', '<span style=\"font-size: 16px; font-family: Poppins;\">SHE Investments supports women with micro businesses to scale to sustainable and impactful SMEs, because they believe that this is where real and long-term social and economic impact can be made. The SME sector is the backbone of any economy, but itâs also a very male-dominated one.</span>', '2', '4', '', 'VISUAL', 'vc_column_text: content', '0', '', '15b2b45ca72a904d1f41a0e354e5c49b', 'optional', '49');
INSERT INTO `nge_icl_strings` VALUES('4926', 'en', 'page-builder-shortcode-strings-60', 'afbe31757eca13bf69152fce1dedad76', '<span style=\"font-size: 16px; font-family: Poppins;\">EZECOM was founded in 2007 to serve the rapidly expanding business and home user internet market in Cambodia. Today it is a leading provider of a wide variety of innovative communication services, including fibre optic, VoIP, virtual private networks and other Internet-based &amp; Cloud solutions. EZECOM owns the fibre optic company Telcotech, which brought very fast and more reliable Internet to Cambodia through the Malaysia-Cambodia-Thailand undersea cable system and its diverse international connectivity.</span>', '2', '5', '', 'VISUAL', 'vc_column_text: content', '0', '', 'a095e6d91928e056224afd50d8c44bb6', 'optional', '76');
INSERT INTO `nge_icl_strings` VALUES('4927', 'en', 'page-builder-shortcode-strings-60', '1c5317ff629d44104b77f8f857b7499c', '<span style=\"font-size: 16px; font-family: Poppins;\">USAID is the lead U.S. Government agency that works to end extreme global poverty and enable resilient, democratic societies to realize their potential. In Cambodia, USAID works with local and international partners on a broad range of programs designed to benefit all Cambodians.</span>', '2', '6', '', 'VISUAL', 'vc_column_text: content', '0', '', '0ccfef5871dea985417418f779d02c52', 'optional', '44');
INSERT INTO `nge_icl_strings` VALUES('4928', 'en', 'page-builder-shortcode-strings-60', '3e1836601f3f068b6d2e034cbcd28e74', '<span style=\"font-size: 16px; font-family: Poppins;\">Development Innovations is a five-year project funded by the United States Agency for International Development (USAID) that helps civil society organizations (CSOs), technology companies and social enterprises to design and use information and communications technology (ICT) solutions that address Cambodiaâs development challenges.</span>', '2', '7', '', 'VISUAL', 'vc_column_text: content', '0', '', '6974a04cd36579deac670dd61612aa56', 'optional', '43');
