INSERT INTO `nge_icl_strings` VALUES('5051', 'en', 'gutenberg-471', '5a88be90d60fb9371a96c0dad0d0fb70', 'For more information on the what documents are needed for sole proprietorship and partnerships go to our registration page.', '10', '12', '', 'LINE', 'core/paragraph', '0', '', 'c5c7b1931b669d472c7166f86b2d1b4d', 'optional', '19');
INSERT INTO `nge_icl_strings` VALUES('5052', 'en', 'gutenberg-472', '62a9f88030bd544adebfc7b5943479de', 'If you decide not register the primary risk is that your business may be shut down or you will be subject to fees and penalties, these may vary depending upon your business activities and size. Additionally you will also be limiting the opportunities for your business to grow by increasing your personal risk (due to personal liability), having an unprotected brand name, being unable to export and increasing the difficulty of accessing financing.', '11', '1', '', 'LINE', 'core/paragraph', '0', '', 'bcf12107a40c26fff5d76ac451ad4d78', 'optional', '73');
INSERT INTO `nge_icl_strings` VALUES('5053', 'en', 'gutenberg-472', '4362c825643a28dde58c4e594f0299a6', 'ClickÂ <a href=\"http://ngeayngeay.co/overview/\">here</a>Â for more information on why you should register.', '11', '2', '', 'VISUAL', 'core/paragraph', '0', '', 'c97d2447006fc6ceef0df62b41a56426', 'optional', '8');
INSERT INTO `nge_icl_strings` VALUES('5054', 'en', 'gutenberg-473', '28ddbe334a2f3bf1332284eed4a8501e', 'The taxes that you have to pay are:', '12', '1', '', 'LINE', 'core/paragraph', '0', '', '63aacef3056fa45da16149bb8522d4eb', 'optional', '8');
