INSERT INTO `nge_icl_strings` VALUES('5093', 'en', 'gutenberg-475', '151e8a05e4ab41a388c64d1bc1a2ac0f', 'It may occur that during the registration process you are asked for a bribe or a âsmall fee,â although this is decreasing, particularly through online payments it still does occur when making in person payments. Often these bribes will be asked to speed up processes or not delay processes, we strongly recommend not paying any bribes as it puts you and your business at risk.', '14', '1', '', 'LINE', 'core/paragraph', '0', '', 'b6983211f2786983c7404554405def31', 'optional', '66');
