INSERT INTO `nge_icl_strings` VALUES('6473', 'en', 'elementor-1156', 'editor-text-editor-f1e96ef', '<p>Filing monthly taxes is a complex process for medium and large taxpayers. We highly recommend you hire a professional to assist you</p>', '47', '2', '', 'VISUAL', 'Text editor', '10', '', '9e51c9ca5c1f7153c1203fa9ff846ed6', 'optional', '22');
INSERT INTO `nge_icl_strings` VALUES('6474', 'en', 'elementor-1156', 'editor-text-editor-2674c50f', '<p>Each month you will have to pay your taxes, this includes your value added tax (VAT), tax on salary, minimum tax on profit, withholding tax and public lighting tax. Additionally you will need to pay your NSSF fees (see here).</p><p>Filing and payment of taxes in Cambodia for medium and large taxpayers is quite complex, we highly recommend that you hire a professional to assist you in this process. However, it is important you understand the basic process, as its your responsibility to ensure that all the taxes are paid correctly.</p><p>For small taxpayers (sole proprietors and partnerships), you only need to fill out one form. The instructions are found on the second page of the form.</p><p>For medium and large taxpayers, there are multiple forms and evidence that is required.</p><p>To file your taxes you need to download the appropriate forms found here. You will need to fill out all the forms and provide the necessary evidence, you need to hand these forms in, with payment to your local tax department for your Khan. Monthly taxes are due on the 20<sup>th</sup> of each month.</p><p><a href=\"http://maskred.com/BIGkeenProject/BK_ngeayngeay/tax-nssf-rates/\">For the latest tax rates, see here.</a></p>', '47', '3', '', 'VISUAL', 'Text editor', '10', '', '5ad28604b8f1693ede18a735316179ae', 'optional', '192');
INSERT INTO `nge_icl_strings` VALUES('6475', 'en', 'Widgets', 'widget body - 301d7953c04519a79e1d41184a8e96a1', '<p>Filing monthly taxes is a complex process for medium and large taxpayers. We highly recommend you hire a professional to assist you</p>', NULL, NULL, '', 'LINE', NULL, '0', '', '6d6d1a594a8a3b920b4aae390734d58e', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('6476', 'en', 'Widgets', 'widget body - e87dbf836596dc4a6c85b59f06d8a4a9', '<p>Each month you will have to pay your taxes, this includes your value added tax (VAT), tax on salary, minimum tax on profit, withholding tax and public lighting tax. Additionally you will need to pay your NSSF fees (see here).</p><p>Filing and payment of taxes in Cambodia for medium and large taxpayers is quite complex, we highly recommend that you hire a professional to assist you in this process. However, it is important you understand the basic process, as its your responsibility to ensure that all the taxes are paid correctly.</p><p>For small taxpayers (sole proprietors and partnerships), you only need to fill out one form. The instructions are found on the second page of the form.</p><p>For medium and large taxpayers, there are multiple forms and evidence that is required.</p><p>To file your taxes you need to download the appropriate forms found here. You will need to fill out all the forms and provide the necessary evidence, you need to hand these forms in, with payment to your local tax department for your Khan. Monthly taxes are due on the 20<sup>th</sup> of each month.</p><p>For the latest tax rates, see here.</p>', NULL, NULL, '', 'LINE', NULL, '0', '', '8402ab2ba1ae398477382c57bdb2f517', '', NULL);
