INSERT INTO `nge_icl_strings` VALUES('6517', 'en', 'elementor-1169', 'editor-text-editor-2674c50f', '<p>The E-VAT system is a new system that has been implemented in Cambodia in 2019, to help the government track VAT payments better. To input your information into e-vat go to <a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">E-VAT Site</a></p><ul><li>Each business will need to create a username and fill in the company information.</li><li>Every business needs to enter every purchase they have been charged VAT for</li><li>Every business needs to upload every sale they have made for the month</li></ul><p>E-VAT deadline is: 20<sup>th</sup> of each month. You will need to attach the E-VAT documents to your monthly tax return</p>', '50', '3', '', 'VISUAL', 'Text editor', '10', '', '736b465a51aba366d35ba41385bf4b3e', 'optional', '95');
