INSERT INTO `nge_icl_strings` VALUES('6558', 'en', 'elementor-1193', 'title-heading-32a3dbb', 'Required Accounting', '57', '1', 'h2', 'LINE', 'Heading', '10', '', 'd4346b3e753feb55a02d6c571e424601', 'optional', '2');
INSERT INTO `nge_icl_strings` VALUES('6559', 'en', 'elementor-1193', 'editor-text-editor-f1e96ef', '<p>Once you register your business one of the most important requirements is clear and proper accounting.</p>', '57', '2', '', 'VISUAL', 'Text editor', '10', '', '3e8e238ad782f921c55eefa3a2b12338', 'optional', '16');
INSERT INTO `nge_icl_strings` VALUES('6560', 'en', 'elementor-1193', 'editor-text-editor-2674c50f', '<p><strong>Small Taxpayers (Sole Proprietors &amp; Partnerships)</strong></p><p>The requirements for small taxpayers are significantly less than they are for medium and large taxpayers. Small Taxpayers are required to have three books:</p><p><a href=\"http://maskred.com/BIGkeenProject/BK_ngeayngeay/wp-content/uploads/2019/07/Expenses-Day-Book-updated.xlsx\"><strong><em>Expenses Day Book</em></strong></a></p><p>This is the gathering of all the operational expenses made by the company, so basically everything that it buys. It can refer to: goods or services, raw materials, employee wages, utility billsâ¦. This record has to be completed daily.</p><p><a href=\"http://maskred.com/BIGkeenProject/BK_ngeayngeay/wp-content/uploads/2019/07/Sales-Day-Book.xlsx\"><strong><em>Sales Day Book</em></strong></a></p><p>The Sales day book, also a daily record, refers to all the goods and services sold by the company.</p><p><strong><em><a href=\"http://maskred.com/BIGkeenProject/BK_ngeayngeay/wp-content/uploads/2019/07/Ngeay-Ngeay-Inventory-template.xlsx\">Inventory Book</a></em></strong></p><p>The inventory book is the count of the inventory of the business.</p><p>Other suggested but not legally required books are:</p><p><strong><em>Accounts Receivable</em></strong></p><p>This is any money that is owed to you</p><p><strong><em>Accounts Payable</em></strong></p><p>This is any money that you owe</p><p><strong><em>Assets Book</em></strong></p><p>Any equipment, furniture or machinery you buy that is over $100</p><p><strong>Medium &amp; Large Taxpayers</strong></p><p>As the tax filing that is required for medium and large taxpayers is significantly more complex than it is for small taxpayers we strongly recommend that you use official accounting procedures such as CIFRS and use a system such as Quickbooks, Xero or Banhji.</p><p>If you are new to book keeping and accounting, we recommend that you hire a professional to help you with these. See here.</p>', '57', '3', '', 'VISUAL', 'Text editor', '10', '', '4dd43764dab6b530d2cc8cffcfd55bfa', 'optional', '210');
