INSERT INTO `nge_icl_strings` VALUES('6757', 'en', 'Widgets', 'widget body - 996736b6506d22908f02154a5f15d3de', '<p><strong>Tax Registration</strong></p>\n<p>You have 2 weeks once your application is approved to go to the General Department of Taxation and register.</p>\n<p>You need to bring all the documents necessary for tax registration. If your business needs to import/export, you will need to ensure you tell the tax department and get an export patent.&nbsp;</p><p>The registration process takes place at the National Tax School Building, level 6.&nbsp;</p>\n<p>Once registered you will receive:</p>\n<p>the Taxpayer Identification Number : this is the number which refers you as a taxpayer with the authorities</p>\n<ul>\n<li>the Patent tax</li>\n<li>the VAT tax.</li>\n</ul>\n<p>Cost of tax registration :</p>\n<p>$200 for registration, $300 for tax patent</p>\n<p></p>\n<p>The National Tax School can be found here</p>\n<p></p>', NULL, NULL, '', 'LINE', NULL, '0', '', 'b68c57e38c8e86487a5c12638cf8704a', '', NULL);
