INSERT INTO `nge_icl_strings` VALUES('10406', 'en', 'elementor-extras', '795dd96d5296aae887a85387baebe8f4', 'A Cultural Response to Cimate Change profiles the work of the artists in the Unfold exhibition and also proposes a number of creative and innovative responses to climate change aimed at stimulating discourse and a wider engagement with the climate debate. The texts by Gerald Bast, Steve Kapelke, Chris Rapley, David Buckland, Chris Wainwright and Helga Kromp-Kolb provoke, within an educational context, a discussion around what are the legitimate agendas for arts education and arts practitioners, in relation to some of the most pressing and urgent issues of our times.', NULL, NULL, '', 'LINE', NULL, '0', '', 'e25e312a6ffdf564db26e80c8bca9467', '', NULL);
