INSERT INTO `nge_icl_strings` VALUES('11655', 'en', 'Widgets', 'widget body - cf8d1e3d2eaf44a1133b8d3ad39c3ed3', '<p>The E-VAT system is a new system that has been implemented in Cambodia in 2019, to help the government track VAT payments better. To input your information into e-vat go to</p><p><a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">https://owp.tax.gov.kh:50005/epaymentowpweb/login</a></p><p>Â </p><ul><li>Each business will need to create a username and fill in the company information.</li><li>Every business needs to enter every purchase they have been charged VAT for</li><li>Every business needs to upload every sale they have made for the month</li></ul><p>E-VAT deadline is: 20<sup>th</sup> of each month. You will need to attach the E-VAT documents to your monthly tax return</p>', NULL, NULL, '', 'LINE', NULL, '0', '', '1a429a835251ec38cadd1ef9b89002e7', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('11656', 'en', 'Widgets', 'widget body - 8224f88d1e9bf7d04e6cb05cc17b8801', '<p>The E-VAT system is a new system that has been implemented in Cambodia in 2019, to help the government track VAT payments better. To input your information into e-vat go to <a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">E-VAT Site</a></p><ul><li>Each business will need to create a username and fill in the company information.</li><li>Every business needs to enter every purchase they have been charged VAT for</li><li>Every business needs to upload every sale they have made for the month</li></ul><p>E-VAT deadline is: 20<sup>th</sup> of each month. You will need to attach the E-VAT documents to your monthly tax return</p>', NULL, NULL, '', 'LINE', NULL, '0', '', '300ecda25f07faeb9bfabea1c2bbbfaa', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('11657', 'en', 'Widgets', 'widget body - 4a91e845668562016cf0f87dfa31cd7a', '<h2>E-VAT</h2>		\n		<p>A brief overview of the newly implemented E-VAT system</p>		\n		<p>The E-VAT system is a new system that has been implemented in Cambodia in 2019, to help the government track VAT payments better. To input your information into e-vat go to <a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">E-VAT Site</a></p><ul><li>Each business will need to create a username and fill in the company information.</li><li>Every business needs to enter every purchase they have been charged VAT for</li><li>Every business needs to upload every sale they have made for the month</li></ul><p>E-VAT deadline is: 20<sup>th</sup> of each month. You will need to attach the E-VAT documents to your monthly tax return</p>', NULL, NULL, '', 'LINE', NULL, '0', '', 'c9f0126dcbf20ffaa96db681f7f93226', '', NULL);
