INSERT INTO `nge_icl_strings` VALUES('11870', 'en', 'Widgets', 'widget body - 6d4b52089ba52d0087b32820b0db457d', '<p><b>HT &amp; P Partners</b> is a Certified Public Accountants firm. We believe in providing clients with only the best of service in terms of quality and professionalism, adopting a personalised, practical and proactive approach to all assignments. Our services are auditing, tax, accounting &amp; advisory, and business incorporation and licensing</p>\n', NULL, NULL, '', 'LINE', NULL, '0', '', 'e602cc6bc1196bc636b8531db0b563f7', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('11871', 'en', 'Widgets', 'widget body - 2aae3f6fb61957e12da222b3c4e8617b', '<p><strong>Suria Digital Marketing</strong> is a creative consultancy specializing in communication and marketing. We provide integrated brand consulting and creative services to help our clients determine how best to develop and enhance the value of their brands to the world. Our services are Search Engine Optimization, Google AdWords, Display Advertising and Social Media Management. </p>\n', NULL, NULL, '', 'LINE', NULL, '0', '', 'f4272e257cf1eaeb1634842f70418c1d', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('11872', 'en', 'Widgets', 'widget body - 04693b971b515c857c5abb5f56c90c83', '<p><strong>Lady Tax</strong> is a women led taxation and business advisory service dedicated to helping women make informed decisions about their financial future in both business and life. Our Goal: woman-entrepreneur have confidence to run business smoothly, legal requirements are covered and convenient to run a business, consulting and develop capacity.</p>\n<p><strong>Love Your Business, Take Care with Your Finances</strong></p>\n', NULL, NULL, '', 'LINE', NULL, '0', '', 'a18e8db2ce3afdb85a9e5de1888c53c3', '', NULL);
INSERT INTO `nge_icl_strings` VALUES('11873', 'en', 'Widgets', 'widget body - 4e0f36bbee2e123a7604893c4b1d0033', '<p>Depending on your business activity you may be required to obtain a licence from the relevant ministry</p>\n', NULL, NULL, '', 'LINE', NULL, '0', '', 'f6047e6184f65bf627eba862abfd11fb', '', NULL);
