INSERT INTO `nge_posts` VALUES('460', '1', '2019-05-26 05:59:00', '0000-00-00 00:00:00', '<!-- wp:paragraph -->\n<p>The costs for registering your business vary depending on the type of company you want to register as:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Sole Proprietorship and Partnership: $270 USD/1,000,080&nbsp;riel</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Register with MoC – $100/400,000 riel</li><li>Register with Tax – $100 USD/400,000 riel</li><li>Register with Labour – $70 USD/280,000 riel</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Limited and Foreign Company: $620 USD/2,500,274 riel</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Register with MoC – $450 USD/1,800,000 riel</li><li>Register with Tax – $100 USD/400,000 riel</li><li>Register with Labour – $70 USD/280,000 riel</li></ul>\n<!-- /wp:list -->', 'How much does it cost to register my business?', '', 'draft', 'closed', 'closed', '', '', '', '', '2019-05-26 05:59:00', '2019-05-26 05:59:00', '', '0', 'http://localhost:8888/ngeayngeay/?post_type=faq&#038;p=460', '0', 'faq', '', '0');
INSERT INTO `nge_posts` VALUES('463', '1', '2019-05-26 06:01:11', '2019-05-26 06:01:11', '<!-- wp:paragraph -->\r\n<p>The costs for registering your business vary depending on the type of company you want to register as:</p>\r\n<!-- /wp:paragraph -->\r\n\r\n<!-- wp:paragraph -->\r\n<p><strong>Sole Proprietorship and Partnership: $270 USD/1,000,080 riel</strong></p>\r\n<!-- /wp:paragraph -->\r\n\r\n<!-- wp:list -->\r\n<ul>\r\n<li>Register with MoC – $100/400,000 riel</li>\r\n<li>Register with Tax – $100 USD/400,000 riel</li>\r\n<li>Register with Labour – $70 USD/280,000 riel</li>\r\n</ul>\r\n<!-- /wp:list -->\r\n\r\n<!-- wp:paragraph -->\r\n<p><strong>Limited and Foreign Company: $620 USD/2,500,274 riel</strong></p>\r\n<!-- /wp:paragraph -->\r\n\r\n<!-- wp:list -->\r\n<ul>\r\n<li>Register with MoC – $450 USD/1,800,000 riel</li>\r\n<li>Register with Tax – $100 USD/400,000 riel</li>\r\n<li>Register with Labour – $70 USD/280,000 riel</li>\r\n</ul>\r\n<!-- /wp:list -->', 'How much does it cost to register my business?', '', 'publish', 'closed', 'closed', '', 'how-much-does-it-cost-to-register-my-business-2', '', '', '2019-05-26 13:23:45', '2019-05-26 06:23:45', '', '0', 'http://localhost:8888/ngeayngeay/?post_type=faq&#038;p=463', '0', 'faq', '', '0');
