INSERT INTO `nge_posts` VALUES('2018', '1', '2019-07-26 08:51:26', '2019-07-26 01:51:26', '<h2>E-VAT</h2>		\n		<p>A brief overview of the newly implemented E-VAT system</p>		\n		<p>The E-VAT system is a new system that has been implemented in Cambodia in 2019, to help the government track VAT payments better. To input your information into e-vat go to <a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">E-VAT Site</a></p><ul><li>Each business will need to create a username and fill in the company information.</li><li>Every business needs to enter every purchase they have been charged VAT for</li><li>Every business needs to upload every sale they have made for the month</li></ul><p>E-VAT deadline is: 20<sup>th</sup> of each month. You will need to attach the E-VAT documents to your monthly tax return</p>', 'E-VAT', '', 'inherit', 'closed', 'closed', '', '1169-revision-v1', '', '', '2019-07-26 08:51:26', '2019-07-26 01:51:26', '', '1169', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/1169-revision-v1/', '0', 'revision', '', '0');
