INSERT INTO `nge_posts` VALUES('2037', '1', '2019-07-26 18:34:51', '2019-07-26 11:34:51', '<h2>តម្រូវការគណនេយ្យ</h2>		\n		<p>នៅពេលអ្នកចុះបញ្ជីពាណិជ្ជកម្ម តម្រូវការដែលចំាបាច់បំផុតមួយគឺគណនេយ្យដែលច្បាស់លាស់ហើយសមរម្យ។</p>\n		<p>អ្នកជាប់ពន្ធខ្នាតតូច(កម្មសិទ្ធតែមួយ ឬ ជាដៃគូរ)</p>\n<p>តម្រូវការចំពោះអ្នកជាប់ពន្ធខ្នាតតូចគឺមានសារសំខាន់តិចជាងអ្នកជាប់ពន្ធខ្នាតមធ្យម និង ធំ។ អ្នកជាប់ពន្ធខ្នាតតូចតម្រូវអោយមានសៀវភៅ៣ក្បាល:</p>\n<ul>\n<li>សៀវភៅកត់ត្រាការទិញប្រចាំថ្ងៃ</li>\n</ul>\n<p>នេះជាការប្រមូលផ្ថុំរាល់ប្រតិបត្តិការចំណាយទាំងអស់របស់ក្រុមហ៊ុន គឺសំដៅលើការទិញ។ វាអាចជាៈ ទំនិញ ឬសេវ៉ាកម្ម វត្តុធាតុដើម ប្រាក់ឈ្នូលនិយោជិក វិក័យប័ត្រប្រើប្រាស់.... ការកត់ត្រានេះត្រូវធ្វើជាប្រចាំថ្ងៃ។</p>\n<ul>\n<li>សៀវភៅកត់ត្រាការលក់ប្រចាំថ្ងៃ</li>\n</ul>\n<p>សៀវភៅលក់ប្រចាំថ្ងៃ អាចជាកំណត់ត្រាប្រចាំថ្ងៃ ដែលសំដៅទៅលើទំនិញនឹងសេវ៉ាកម្មទាំងអស់ ដែលបានលក់ដូរ ដោយក្រុមហ៊ុន។</p>\n<ul>\n<li>សៀវភៅកត់ត្រាសន្និធិ</li>\n</ul>\n<p>សៀវភៅកត់ត្រាសន្និធិ គឺសំដៅលើការកត់ត្រាលើអ្វីដែលក្រុមហ៊ុនមាននៅក្នុងស្តុក។</p>\n<p>ការស្នើរសុំដ៏ទៃទៀត តែមិនមែនជាតម្រូវការផ្នែកច្បាប់រួមមានៈ</p>\n<p>គណនីយត្រូវទទួល</p>\n<p>នេះជាប្រាក់ដែលគេជំពាក់អ្នក</p>\n<p>គណនីយត្រូវបង់</p>\n<p>នេះជាប្រាក់ដែលអ្នកជំពាក់គេ</p>\n<p>សៀវភៅទ្រព្យសម្បិត្តិ</p>\n<p>រាល់សំភារះ គ្រឿងសង្ហារឹម ឬគ្រឿងចក្រដែលអ្នកទិញដែលមានតំលៃលើស១០០ដុល្លា</p>\n<p>អ្នកជាប់ពន្ធខ្នាតមធ្យម និង ធំ​</p>\n<p>ក្នុងនាមជាអ្នកជាប់ពន្ធដែលជាតម្រូវការសំរាប់អ្នកជាប់ពន្ធខ្នាតមធ្យម និងធំគឺមានភាពចំាបាច់ហើយស្មុគស្មាញច្រើនជាងអ្នកជាប់ពន្ធខ្នាតតូច ដែលយើងសូមផ្ថល់យោបល់អោយប្រើប្រាស់និតិវិធីគណនេយ្យផ្លូវការដូចជា CIFRS ហើយប្រើប្រាស់ប្រពន្ធ័ដូចជា quickbooks, xero ឬ banhji។</p>\n<p>ប្រសិនបើអ្នកជាអ្នកថ្មីក្នុងការ កត់ត្រាសៀវភៅ ហើយធ្វើគណនេយ្យ យើងសូមណែនាំអោយអ្នកជួលអ្នកដែលមានវិជ្ជាជីវះដើម្បីជួយអ្នកលើការងារទាំងនេះ។ សូមមើលនៅទីនេះ។</p>', 'តម្រូវការគណនេយ្យ', '', 'inherit', 'closed', 'closed', '', '2036-revision-v1', '', '', '2019-07-26 18:34:51', '2019-07-26 11:34:51', '', '2036', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2036-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2038', '1', '2019-07-26 18:36:34', '2019-07-26 11:36:34', '<h2>ពន្ធប្រចាំខែ និងរបបសន្តិសុខសង្គម</h2>\nពន្ធប្រចាំខែគឺផុតកំណត់នៅថ្ងៃទី២០ជារៀងរាល់ខែ, របបសន្តិសុខសង្គមគឺផុតកំណត់នៅថ្ងៃទី១៥ជារៀងរាល់ខែ\n\nខាងក្រោមនេះអ្នកនឹងឃើញតារាងសង្ខេបនៃអត្រាពន្ធប្រចាំខែនិងអត្រាបេឡាជាតិរបបសន្តិសុខសង្គម.រាល់ពន្ធប្រចាំខែត្រូវដល់ថ្ងៃទី ២០ នៃខែនីមួយៗ&nbsp;&nbsp;បេឡាជាតិរបបសន្តិសុខសង្គមត្រូវដល់ថ្ងៃទី ១៥ នៃខែនីមួយៗ។\n\nប្រសិនបើអ្នកចង់ប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នកសូមទៅរកអ្នកគណនាពន្ធរបស់យើងហើយការគណនាពន្ធនិងអនុញ្ញាតឱ្យវាប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នក&nbsp;\n<h2>ពន្ធអាករនិងអប្បបរមា</h2>\n<table>\n<colgroup>\n<col span=\"1\">\n							</colgroup>\n<thead>\n<tr>\n<th>\n							ពន្ធអាករនិងអប្បបរមា</th>\n<th>\n							អត្រា</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធលើតម្លៃបន្ថែម</td>\n<td data-title=\"អត្រា\" colspan=\"2\">\n					10%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធអប្បបរមា (មុនចំណេញ)</td>\n<td data-title=\"អត្រា\">\n					1%</td>\n</tr>\n</tbody>\n</table>\n<h2>ពន្ធលើប្រាក់ខែ</h2>\n<table>\n<colgroup>\n<col span=\"1\">\n							</colgroup>\n<thead>\n<tr>\n<th>\n							ពន្ធលើប្រាក់ខែ</th>\n<th>\n							អត្រា</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					0 - 1,200,000 KHR (0 - 300 USD)</td>\n<td data-title=\"អត្រា\" colspan=\"2\">\n					0%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					1,200,001 - 2,000,000 KHR (300 - 500)</td>\n<td data-title=\"អត្រា\">\n					5%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					2,000,001 - 8,500,000 KHR (500 - 2,125 USD)</td>\n<td data-title=\"អត្រា\">\n					10%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					8,500,001 - 12,500,000 KHR (2,125 - 3,125 USD)</td>\n<td data-title=\"អត្រា\">\n					15%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					ច្រើនជាង12,500,001 KHR (ច្រើនជាង 3,125 USD)</td>\n<td data-title=\"អត្រា\">\n					20%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					អាននិវាសនជន (អត្រាការប្រាក់)</td>\n<td data-title=\"អត្រា\">\n					20%</td>\n</tr>\n</tbody>\n</table>\n<h2>ពន្ធកាត់ទុក</h2>\n<table>\n<colgroup>\n<col span=\"1\">\n							</colgroup>\n<thead>\n<tr>\n<th>\n							ពន្ធកាត់ទុក</th>\n<th>\n							អត្រា</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					ជួល</td>\n<td data-title=\"អត្រា\" colspan=\"2\">\n					10%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					 សេវាកម្ម</td>\n<td data-title=\"អត្រា\">\n					15%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					ការទូទាត់ការប្រាក់ (ដោយមិនរាប់បញ្ចូលការប្រាក់ដែលបង់ដោយធនាគារក្នុងស្រុក)</td>\n<td data-title=\"អត្រា\">\n					15%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					ថ្លៃឈ្នួល (ភាគលាភ)</td>\n<td data-title=\"អត្រា\">\n					15%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					ការប្រាក់បានបង់លើការដាក់ប្រាក់មានកាលកំណត់ (ដោយធនាគារក្នុងស្រុក)</td>\n<td data-title=\"អត្រា\">\n					6%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធកាត់ទុក\">\n					ការប្រាក់បានបង់លើប្រាក់បញ្ញើមានកាលកំណត់មិនកំណត់ (ដោយធនាគារក្នុងស្រុក)</td>\n<td data-title=\"អត្រា\">\n					4%</td>\n</tr>\n</tbody>\n</table>\n<h2>ពន្ធផ្សេងៗ</h2>\n<table>\n<colgroup>\n<col span=\"1\">\n							</colgroup>\n<thead>\n<tr>\n<th>\n							ពន្ធផ្សេងៗ</th>\n<th>\n							អត្រា</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធអត្ថប្រយោជន៍បន្ថែម</td>\n<td data-title=\"អត្រា\" colspan=\"2\">\n					20%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធផ្សេងៗ \">\n					​ពន្ធបំភ្លឺសាធារណៈ</td>\n<td data-title=\"អត្រា\">\n					3%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធស្នាក់នៅ</td>\n<td data-title=\"អត្រា\">\n					2%</td>\n</tr>\n<tr>\n<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធលើការជួលអចលនទ្រព្យ</td>\n<td data-title=\"អត្រា\">\n					10%</td>\n</tr>\n</tbody>\n</table>\n<h2>អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម</h2>\n<table>\n<colgroup>\n<col span=\"1\">\n							</colgroup>\n<thead>\n<tr>\n<th>\n							បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)</th>\n<th>\n							អត្រា</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td data-title=\"បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)\">\n					គ្រោងការណ៍ហានិភ័យការងារ</td>\n<td data-title=\"អត្រា\" colspan=\"2\">\n					0.8% នៃប្រាក់ឈ្នួល</td>\n</tr>\n<tr>\n<td data-title=\"បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)\">\n					គ្រោងការណ៍ថែរក្សាសុខភាព</td>\n<td data-title=\"អត្រា\">\n					2.6% នៃប្រាក់ឈ្នួល</td>\n</tr>\n</tbody>\n</table>', 'ពន្ធប្រចាំខែ និងរបបសន្តិសុខសង្គម', '', 'publish', 'closed', 'closed', '', 'tax-nssf-rates', '', '', '2019-08-01 15:03:49', '2019-08-01 08:03:49', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/tax-nssf-rates/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2039', '1', '2019-07-26 18:36:35', '2019-07-26 11:36:35', '<h2>Monthly Tax &amp; NSSF Rates</h2>		\n		<p>Monthly Taxes are due at the 20th of each month, NSSF is due on the 15th of each month</p>\n		<p>Below you will find summary tables of the monthly tax rates and NSSF rates. All monthly taxes are due on the 20th of each month, NSSF is due on the 15th of each month.</p>\n<p>If you would like to estimate your monthly taxes, head to our tax calculator and let it estimate your monthly tax</p>\n			<h2>VAT & Minimum Tax</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							VAT & Minimum Tax						\n				</th>\n			<th>\n							Rate						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"VAT &amp; Minimum Tax\">\n					Value Added Tax				\n		</td>		<td data-title=\"Rate\" colspan=\"2\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"VAT &amp; Minimum Tax\">\n					Minimum Tax (Pre-Profit)				\n		</td>		<td data-title=\"Rate\">\n					1%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>Tax on Salary</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							Tax on Salary						\n				</th>\n			<th>\n							Rate						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"Tax on Salary\">\n					0 - 1,200,000 KHR (0 - 300 USD) 				\n		</td>		<td data-title=\"Rate\" colspan=\"2\">\n					0%				\n		</td></tr><tr>\n				<td data-title=\"Tax on Salary\">\n					1,200,001 - 2,000,000 KHR (300 - 500)				\n		</td>		<td data-title=\"Rate\">\n					5%				\n		</td></tr><tr>\n				<td data-title=\"Tax on Salary\">\n					2,000,001 - 8,500,000 KHR (500 - 2,125 USD)				\n		</td>		<td data-title=\"Rate\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"Tax on Salary\">\n					8,500,001 - 12,500,000 KHR (2,125 - 3,125 USD)				\n		</td>		<td data-title=\"Rate\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"Tax on Salary\">\n					more than 12,500,001 KHR (more than 3,125 USD)				\n		</td>		<td data-title=\"Rate\">\n					20%				\n		</td></tr><tr>\n				<td data-title=\"Tax on Salary\">\n					Non - Resident (flat rate)				\n		</td>		<td data-title=\"Rate\">\n					20%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>Withholding Tax</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							Withholding Tax						\n				</th>\n			<th>\n							Rate						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"Withholding Tax\">\n					Rent				\n		</td>		<td data-title=\"Rate\" colspan=\"2\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"Withholding Tax\">\n					Services				\n		</td>		<td data-title=\"Rate\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"Withholding Tax\">\n					Interest Payments (excluding interest paid by a domestic bank)				\n		</td>		<td data-title=\"Rate\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"Withholding Tax\">\n					Royalties (Dividends)				\n		</td>		<td data-title=\"Rate\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"Withholding Tax\">\n					Interest paid on fixed term deposit (by domestic bank)				\n		</td>		<td data-title=\"Rate\">\n					6%				\n		</td></tr><tr>\n				<td data-title=\"Withholding Tax\">\n					Interest paid on non-fixed term deposit (by a domestic bank)				\n		</td>		<td data-title=\"Rate\">\n					4%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>Other Taxes</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							Other Taxes						\n				</th>\n			<th>\n							Rate						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"Other Taxes\">\n					Fringe Benefit Tax				\n		</td>		<td data-title=\"Rate\" colspan=\"2\">\n					20%				\n		</td></tr><tr>\n				<td data-title=\"Other Taxes\">\n					Public Lighting Tax				\n		</td>		<td data-title=\"Rate\">\n					3%				\n		</td></tr><tr>\n				<td data-title=\"Other Taxes\">\n					Accomodation Tax				\n		</td>		<td data-title=\"Rate\">\n					2%				\n		</td></tr><tr>\n				<td data-title=\"Other Taxes\">\n					Tax on Property Rental				\n		</td>		<td data-title=\"Rate\">\n					10%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>NSSF Rates</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							NSSF (due on the 15th)						\n				</th>\n			<th>\n							Rate						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"NSSF (due on the 15th)\">\n					Occupational Risk Scheme				\n		</td>		<td data-title=\"Rate\" colspan=\"2\">\n					0.8% of wage				\n		</td></tr><tr>\n				<td data-title=\"NSSF (due on the 15th)\">\n					Health Care Scheme				\n		</td>		<td data-title=\"Rate\">\n					2.6% of wage				\n		</td></tr>\n			</tbody>\n		</table>', 'Tax & NSSF Rates', '', 'inherit', 'closed', 'closed', '', '2038-revision-v1', '', '', '2019-07-26 18:36:35', '2019-07-26 11:36:35', '', '2038', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2038-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2040', '1', '2019-07-26 18:37:36', '2019-07-26 11:37:36', '<h2>Annual Taxes</h2>		\n		<p>Every year on the 31st of March annual taxes need to be filed for every business. This is quite a complex process and we recommend hiring a professional firm to assist</p>\n		<p><strong>Annual tax on profit</strong></p>\n<p>At the end of the year, you will have to pay a tax for a total amount of 20% of your profit. You can then deduce the 1% you paid every month during the year.</p>\n<p>Annual taxable profit in USD for a limited company:<br />&lt; 1,463 USD                     0%<br />1,463 – 3,658 USD          5%<br />3,658 – 24,878 USD       10%<br />24,878 – 36,585 USD     15%<br />&gt; 36,585 USD                   20%</p>\n<p> </p>\n<p><strong>Small Taxpayers</strong></p>\n<p>The patent tax for sole proprietorships and partnerships is $100, which is approx. KHR400,000.</p>\n<p><strong>Medium &amp; Large Taxpayers</strong></p>\n<p>The patent tax is a fixed annual fee. It depends on which taxpayer category your company belongs to (small, medium or large). These are the official patent tax amounts:</p>\n<p>Medium Taxpayer          KHR1,200,000/USD300</p>\n<p>Large Taxpayer             KHR3,000,000 or KHR5,000,000*/ USD750 or USD1,250</p>\n<p>*If the annual turnover of the Large Taxpayer exceeds KHR10,000 million (USD2.5 million) then the Patent Tax payable will be USD1,250.</p>\n<p>If the annual turnover of the Large Taxpayer is less than KHR10,000 million (USD2.5 million) the Patent Tax payable will be USD750.</p>', 'Annual Taxes', '', 'publish', 'closed', 'closed', '', 'annual-taxes', '', '', '2019-07-26 18:37:37', '2019-07-26 11:37:37', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/annual-taxes/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2041', '1', '2019-07-26 18:37:37', '2019-07-26 11:37:37', '<h2>Annual Taxes</h2>		\n		<p>Every year on the 31st of March annual taxes need to be filed for every business. This is quite a complex process and we recommend hiring a professional firm to assist</p>\n		<p><strong>Annual tax on profit</strong></p>\n<p>At the end of the year, you will have to pay a tax for a total amount of 20% of your profit. You can then deduce the 1% you paid every month during the year.</p>\n<p>Annual taxable profit in USD for a limited company:<br />&lt; 1,463 USD                     0%<br />1,463 – 3,658 USD          5%<br />3,658 – 24,878 USD       10%<br />24,878 – 36,585 USD     15%<br />&gt; 36,585 USD                   20%</p>\n<p> </p>\n<p><strong>Small Taxpayers</strong></p>\n<p>The patent tax for sole proprietorships and partnerships is $100, which is approx. KHR400,000.</p>\n<p><strong>Medium &amp; Large Taxpayers</strong></p>\n<p>The patent tax is a fixed annual fee. It depends on which taxpayer category your company belongs to (small, medium or large). These are the official patent tax amounts:</p>\n<p>Medium Taxpayer          KHR1,200,000/USD300</p>\n<p>Large Taxpayer             KHR3,000,000 or KHR5,000,000*/ USD750 or USD1,250</p>\n<p>*If the annual turnover of the Large Taxpayer exceeds KHR10,000 million (USD2.5 million) then the Patent Tax payable will be USD1,250.</p>\n<p>If the annual turnover of the Large Taxpayer is less than KHR10,000 million (USD2.5 million) the Patent Tax payable will be USD750.</p>', 'Annual Taxes', '', 'inherit', 'closed', 'closed', '', '2040-revision-v1', '', '', '2019-07-26 18:37:37', '2019-07-26 11:37:37', '', '2040', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2040-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2042', '1', '2019-07-26 18:39:16', '2019-07-26 11:39:16', '<h2>អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម</h2>		\n		<p>មានការបង់ប្រាក់២បែបចំពោះបេឡាជាតិរបបសន្តិសុខសង្គម ដែលអាចធ្វើបានតាមខែនីមួយៗដោយនយោជក់។</p>\n		<p>គម្រោងហានិភ័យមុខងារ</p>\n<p>អត្រាសំរាប់ហានិភ័យមុខងាគឺមានតំលៃ០,៨% នៃប្រាក់ខែសរុបកម្មករ។</p>\n<p>គម្រោងថែរក្សាសុខភាព</p>\n<p>គម្រោងថែរក្សាសុខភាពគឺមានតំលៃ២,៦%នៃប្រាក់ខែសរុបកម្មករ។</p>\n<p>កម្មករទាំងអស់ត្រូវបានចុះឈ្មោះជាមួយរបបជាតិសន្តិសុខសង្គមក្នុងរយៈពេល៣ថ្ងៃចាប់ពីថ្ងៃចាប់ផ្តើមធ្វើការងារ។​</p>\n<p>កាលកំណត់បង់ប្រាក់បេឡាជាតិរបបសន្តិសុខសង្គមៈ ថ្ងៃទី១៥ នៃខែនីមួយៗ</p>\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=ministry%20of%20labour&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"ministry of labour\"></iframe>', 'អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម', '', 'publish', 'closed', 'closed', '', '%e1%9e%a2%e1%9e%8f%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%94%e1%9f%81%e1%9e%a1%e1%9e%b6%e1%9e%87%e1%9e%b6%e1%9e%8f%e1%9e%b7%e1%9e%9a%e1%9e%94%e1%9e%94%e1%9e%9f%e1%9e%93%e1%9f%92%e1%9e%8f%e1%9e%b7%e1%9e%9f', '', '', '2019-07-26 18:39:18', '2019-07-26 11:39:18', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/%e1%9e%a2%e1%9e%8f%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%94%e1%9f%81%e1%9e%a1%e1%9e%b6%e1%9e%87%e1%9e%b6%e1%9e%8f%e1%9e%b7%e1%9e%9a%e1%9e%94%e1%9e%94%e1%9e%9f%e1%9e%93%e1%9f%92%e1%9e%8f%e1%9e%b7%e1%9e%9f/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2043', '1', '2019-07-26 18:39:17', '2019-07-26 11:39:17', '<h2>អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម</h2>		\n		<p>មានការបង់ប្រាក់២បែបចំពោះបេឡាជាតិរបបសន្តិសុខសង្គម ដែលអាចធ្វើបានតាមខែនីមួយៗដោយនយោជក់។</p>\n		<p>គម្រោងហានិភ័យមុខងារ</p>\n<p>អត្រាសំរាប់ហានិភ័យមុខងាគឺមានតំលៃ០,៨% នៃប្រាក់ខែសរុបកម្មករ។</p>\n<p>គម្រោងថែរក្សាសុខភាព</p>\n<p>គម្រោងថែរក្សាសុខភាពគឺមានតំលៃ២,៦%នៃប្រាក់ខែសរុបកម្មករ។</p>\n<p>កម្មករទាំងអស់ត្រូវបានចុះឈ្មោះជាមួយរបបជាតិសន្តិសុខសង្គមក្នុងរយៈពេល៣ថ្ងៃចាប់ពីថ្ងៃចាប់ផ្តើមធ្វើការងារ។​</p>\n<p>កាលកំណត់បង់ប្រាក់បេឡាជាតិរបបសន្តិសុខសង្គមៈ ថ្ងៃទី១៥ នៃខែនីមួយៗ</p>\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=ministry%20of%20labour&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"ministry of labour\"></iframe>', 'អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម', '', 'inherit', 'closed', 'closed', '', '2042-revision-v1', '', '', '2019-07-26 18:39:17', '2019-07-26 11:39:17', '', '2042', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2042-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2044', '1', '2019-07-26 18:40:42', '2019-07-26 11:40:42', '<h2>ការចុះឈ្មោះនៅក្រសួងការងារ</h2>\nរាល់អាជីវកម្មដែលបានចុះបញ្ជីជាមួយក្រសួងពាណិជ្ជកម្ម និងអគ្គនាយកដ្ឋានពន្ធដារចាំបាច់ត្រូវចុះឈ្មោះជាមួយនឹងក្រសួងការងារ។\n\nដើម្បីចុះឈ្មោះនៅក្រសួងការងារអ្នកនឹងត្រូវការ៖\n<ul>\n 	<li>&nbsp;បណ្ណ័ពន្ធប៉ាតង់</li>\n 	<li>&nbsp;វិញ្ញាបនប័ត្របញ្ជាក់ការចុះឈ្មោះក្នុងបញ្ជីពាណិជ្ជកម្ម</li>\n 	<li>បញ្ជីនិយោជិក (សូមមើលគំរូនៅទីនេះ)</li>\n 	<li>ទម្រង់បែបបទរបស់ក្រសួងការងារសម្រាប់បំពេញ</li>\n</ul>\nតម្រូវឲ្យយកឯកសារទាំងអស់ទៅអគារដែលគេហៅថា“ ការងារទូទៅ“ រឺអគ្គការងារ នៅជាន់ទី៣។ បន្ទប់អគ្គការងារទូទៅស្ថិតនៅចុងគេខាងស្តាំ។\n\n<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=ministry%20of%20labour&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"ministry of labour\"></iframe>', 'ការចុះបញ្ជីក្នុងក្រសួងការងារ', '', 'publish', 'closed', 'closed', '', 'mol-registration', '', '', '2019-08-01 16:11:27', '2019-08-01 09:11:27', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/%e1%9e%80%e1%9e%b6%e1%9e%9a%e1%9e%85%e1%9e%bb%e1%9f%87%e1%9e%94%e1%9e%89%e1%9f%92%e1%9e%87%e1%9e%b8%e1%9e%80%e1%9f%92%e1%9e%93%e1%9e%bb%e1%9e%84%e1%9e%80%e1%9f%92%e1%9e%9a%e1%9e%9f%e1%9e%bd%e1%9e%84/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2045', '1', '2019-07-26 18:40:43', '2019-07-26 11:40:43', '<h2>ការចុះបញ្ជីក្នុងក្រសួងការងារ</h2>		\n		<p>រាល់អាជីវកម្មដែលបានចុះបញ្ជីជាមួយក្រសួងពាណិជ្ជកម្ម និង អគ្គនាយកដ្ឋានពន្ធដារត្រូវតែចុះបញ្ជីជាមួយក្រសួងការងារ។</p>\n		<p>ដើម្បីចុះបញ្ជីក្រសួងការងារអ្នកត្រូវតែមាន</p>\n<ul>\n<li>ពន្ធប៉ាតង់</li>\n<li>វិញ្ញាបនបត្រ័ការចុះបញ្ជីក្រុមហ៊ុន</li>\n<li>បញ្ជីនយោជិក (សូមមើលទំរង់នៅទីនេះ)</li>\n<li>បំពេញបែបបទក្រសួងការងារ​</li>\n</ul>\n<p>យកឯកសារទាំងអស់ទៅកាន់អគារដែលមានឈ្មោះ ថា អគ្គការងារ ជាន់ទី៣។ អគារអគ្គការងារស្តិតនៅខាងក្រោយ ផ្នែកខាងឆ្វេង</p>\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=ministry%20of%20labour&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"ministry of labour\"></iframe>', 'ការចុះបញ្ជីក្នុងក្រសួងការងារ', '', 'inherit', 'closed', 'closed', '', '2044-revision-v1', '', '', '2019-07-26 18:40:43', '2019-07-26 11:40:43', '', '2044', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2044-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2046', '1', '2019-07-26 18:42:07', '2019-07-26 11:42:07', '<h2>ថ្ងៃឈប់សម្រាក់</h2>\nមានថ្ងៃឈប់សម្រាកមួយចំនួនដែលបុគ្គលិកមានសិទ្ធឈប់សម្រាកនៅក្នុងប្រទេកម្ពុជា។\n\n<strong>ថ្ងៃឈប់សម្រាកប្រចាំឆ្នាំ</strong>\n\nថ្ងៃឈប់សម្រាកប្រចាំឆ្នាំត្រូវបានផ្តល់ទៅកាន់បុគ្គលិកទាំងអស់នៅពេលដែលពួកគេបានបំពេញការងាររបស់ពួកគេបានរយៈពេល១ឆ្នាំ។​ ការឈប់សម្រាកប្រចាំឆ្នាំគឺ​ គិត តាមអត្រាប្រាក់ឈ្នួលដែលមានរយៈពេល១៨ថ្ងៃ ប្រសិនបើបុគ្គលិកធ្វើការ ៤៨ម៉ោង ក្នុងមួយសប្តាហ៍ តែប្រសិនបើបុគ្គលិកធ្វើការ៤០ម៉ោងក្នុងមួយសប្តាហ៍ នោះបុគ្គលិក នឹងទទួលបានថ្ងៃឈប់សម្រាកប្រចាំឆ្នាំចំនួន១៥ថ្ងៃ។\n\nសម្រាប់ការបំពេញការងារលើសពី៣ឆ្នាំ និយោជិកមានសិទ្ធិទទួលបានថ្ងៃឈប់ សម្រាកបន្ថែមនៃថ្ងៃឈប់សម្រាកប្រចាំឆ្នាំក្នុងមួយឆ្នាំ។\n\n<strong>ការឈប់សម្រាកប្រចាំសប្តាហ៍</strong>\n\nនិយោជិកទាំងអស់មានសិទ្ធិទទួលបានការឈប់សម្រាក ២៤ម៉ោងក្នុងមួយសប្តាហ៍។\n\n<strong>ច្បាប់សម្រាកឈឺ</strong>\n\nតាមច្បាប់ការងារ រាល់អាជីវកម្មទាំងអស់តម្រូវឲ្យផ្តល់ច្បាប់សម្រាកឈឺទៅកាន់ បុគ្គលិកទាំងអស់ ប្រសិនបើបុគ្គលិកមានលិខិតបញ្ជាក់ពីគ្រូពេទ្យដែលតម្រូវឲ្យ សម្រាកលើសពី៦ខែ តែបើការសម្រាកលើសពី៦ខែ ម្ចាស់អាជីវកម្មមានសិទ្ធបញ្ចប់ កិច្ចសន្យារបស់បុគ្គលិក។ រាល់ការបង់ប្រាក់សម្រាប់បុគ្គលិកនៅពេលពេលបុគ្គលិក ឈឺត្រូមានល័ក្ខខណ្ឌដូចខាងក្រោម៖\n<ul>\n 	<li>ការឈប់សម្រាករយៈពេល១ខែៈ ប្រាក់ខែ១០០%</li>\n 	<li>ការឈប់សម្រាកពី២ ទៅ៣ខែៈ ប្រាក់ខែ៦០%</li>\n 	<li>ការឈប់សម្រាកពី៤ខែ ឬលើសពី៤ខែៈ មិនតម្រូវឲ្យមានប្រាក់ខែ</li>\n</ul>\n<strong>ច្បាប់មាតុភាព</strong>\n\nបុគ្គលិកស្រ្តីទាំងអស់មានសិទ្ធទទួលបានច្បាប់មាតុភាពចំនួន៩០ថ្ងៃ ហើយបុគ្គលិក មានសិទ្ធទទួលបានប្រាក់ខែ និងសារៈប្រយោជន៍ពីក្រុមហ៊ុន។\n\n<strong>ថ្ងៃឈប់សម្រាកសាធារណៈ</strong>\n\nបុគ្គលិកទាំងអស់មានសិទ្ធឈប់សម្រាកទៅតាមថ្ថៃឈប់សម្រាកនៃប្រទេសទៅតាមការកំណត់របស់រាជរដ្ឋាភិបាល។ ក្នុងករណីដែលថ្ងៃឈប់សម្រាកនោះចំថ្ងៃអាទិត្យ បុគ្គលិកត្រូវទទួលបានសិទ្ធឈប់សម្រាកនៅថ្ងៃច័ន្ទបន្តទៀត។​\n\nប្រសិនបើអាជីវកម្មត្រូវបើដំណើរការនៅពេលបុណ្យជាតិនានា បុគ្គលិកអាច ជ្រើសរើសធ្វើការដោយការស្ម័គ្រចិត្ត តែពួកគេត្រូវទទួលបានកម្រៃបន្ថែម ២០០% លើសពីប្រាក់ខែរបស់ពួកគេ។', 'ថ្ងៃឈប់សម្រាក់', '', 'publish', 'closed', 'closed', '', 'leave', '', '', '2019-08-01 23:05:24', '2019-08-01 16:05:24', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/%e1%9e%80%e1%9e%b6%e1%9e%9a%e1%9e%88%e1%9e%94%e1%9f%8b%e1%9e%9f%e1%9f%86%e1%9e%9a%e1%9e%b6%e1%9e%80/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2047', '1', '2019-07-26 18:42:08', '2019-07-26 11:42:08', '<h2>ការឈប់សំរាក</h2>		\n		<p>There are various types of leave that employees are obligated to receive in Cambodia</p>\n		<p><strong>ការឈប់សំរាកប្រចាំឆ្នាំ</strong></p>\n<p>ការឈប់សំរាកប្រចាំឆ្នាំត្រូវបានផ្ថល់អោយនយោជិកទាំងអស់នៅពេលដែលពួកគេបានបំពេញការងារបាន១រយះពេលឆ្នាំ។ ការឈប់សំរាកប្រចាំឆ្នាំ មានសមាត្រ ចំនួន១៨ថ្ងៃសំរាប់ការបំពេញការងារបាន១ឆ្នាំ ដោយផ្អែកលើការធ្វើការ៤៨ម៉ោងក្នុង១សប្តាហ៍។ ប្រសិនបើនយោជិកធ្វើការតិចជាង៤៨ម៉ោង ឧទាហរណ៍ ៤០ម៉ោង នយោជិកត្រូវបានផ្ថល់ការឈប់ប្រចាំឆ្នាំចំនួន១៥ថ្ងៃ។</p>\n<p>រៀងរាល់៣ឆ្នាំម្តងនៃការបំពេញការងារ នយោយជិកមានសិទ្ធទទួលបានការឈប់ប្រចាំឆ្នាំបន្ថែមចំនួន១ថ្ងៃ។</p>\n<p><strong>ការសំរាកជាសប្តាហ៍</strong></p>\n<p>នយោជិកទាំងអស់មានសិទ្ធទទួលបានការឈប់សំរាកចំនួន២៤ម៉ោងបន្តបន្ទាប់គ្នាក្នុង១សប្តាហ៍</p>\n<p><strong>ការឈប់សំរាកដោយសារជំងឺ</strong></p>\n<p>ច្បាប់ការងារទាមទារអោយអាជីវកម្មផ្តល់ការឈប់សំរាកដោយសារជំងឺទៅកាន់នយោជិក ប្រសិនបើពួកគេមានលិខិតពីវេជ្ជបណ្ឌិតដែលមានវិញ្ញាប័នបត្រត្រឹមត្រូវដែលអាចឈប់បាន​រយះពេលរហូតដល់៦ខែ។ ក្រោយពីរយះពេល៦ខែ អាជីវកម្មអាចបញ្ឈប់នយោជិកបាន។ អត្រានៃការបង់ប្រាក់អោយនយោជិកក្នុងអំឡុងពេលឈឺត្រូវបានចែងដូចខាងក្រោម:</p>\n<ul>\n<li>អំឡុងពេល១ខែនៃជំងឺ ទទួលបានប្រាក់ឈ្នូល១០០%</li>\n<li>អំឡុងពេលខែទី២ ទីនៃជំងឺ ទទួលបានប្រាក់ឈ្នូល១០០៦០%</li>\n<li>អំឡុងពេលខែទី៤ ហើយវាច្រើនជាងនោះ មិនត្រូវបានផ្តល់ប្រាក់ខែ</li>\n</ul>\n<p><strong>ការឈប់មាតុភាព </strong></p>\n<p>ស្រី្តទាំងអស់ត្រូវបានមានសិទ្ធឈប់មាតុភាពចំនួន៩០ថ្ងៃ។ នយោជិកត្រូវបានមានសិទ្ធិទទួលបានប្រាក់ខែ និង ផលប្រយោជន៍នានាចំនួនពាក់កណ្តាល។</p>\n<p><strong>ថ្ងៃឈប់សំរាកជាសាធារណៈ </strong></p>\n<p>រាល់នយោជិកទាំងអស់មានសិទ្ធទទួលបានថ្ងៃឈប់សំរាកជាសាធារណៈ​ដោយយោងតាមថ្ងៃឈប់សំរាកជាសាធារណៈ​ដែលចែងក្នុងអនុក្រិតដែលចេញដោយរដ្ឋាភិបាល។ ក្នុងករណីដែលថ្ងៃឈប់សំរាកជាសាធារណៈ​ចំថ្ងៃអាទិត្យ នយោជិកអាចមានសិទ្ធិឈប់សំរាកជំនួសនៅថ្ងៃចន្ទ័ដែលជាថ្ងៃធ្វើការ។</p>\n<p>ប្រសិនបើអាជីវកម្មត្រូវបានទាមទារអោយបើកបំរើការនៅថ្ងៃឈប់សំរាកជាសាធារណះ នយោជិកអាចធ្វើការងារដោយស្មគ្រ័ចិត្តប៉ុន្តែមានសិទ្ធិទទួលបានប្រាក់២០០%នៃប្រាក់ឈ្នូលធម្មតា។</p>', 'ការឈប់សំរាក', '', 'inherit', 'closed', 'closed', '', '2046-revision-v1', '', '', '2019-07-26 18:42:08', '2019-07-26 11:42:08', '', '2046', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2046-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2048', '1', '2019-07-26 18:42:49', '2019-07-26 11:42:49', '<h2>Import/Export</h2>		\n		<p>To be able to import/export it is important that you have applied for this in your tax patent with the General Department of Tax</p>\n		<p>For importing and exporting, you need to register your patent as an import/export patent when you register with the General Department of Tax.</p>\n<p>When you are ready to do your first import/export you need to register with the Automated System for Customs Data (ASYCUDA). To do this you will need to go to the customs office located at No. 6-8, Preah Norodom Blvd., Sangkat Phsar Thmei III, Khan Daun Penh, Phnom Penh, Cambodia.</p>\n<p>If you are importing/exporting any items deemed sensitive by the government then you will need to obtain a licence from the relevant ministry.</p>\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=customs%20office%2C%20phnom%20penh&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"customs office, phnom penh\"></iframe>', 'Export', '', 'publish', 'closed', 'closed', '', 'export', '', '', '2019-07-26 18:42:51', '2019-07-26 11:42:51', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/export/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2049', '1', '2019-07-26 18:42:50', '2019-07-26 11:42:50', '<h2>Import/Export</h2>		\n		<p>To be able to import/export it is important that you have applied for this in your tax patent with the General Department of Tax</p>\n		<p>For importing and exporting, you need to register your patent as an import/export patent when you register with the General Department of Tax.</p>\n<p>When you are ready to do your first import/export you need to register with the Automated System for Customs Data (ASYCUDA). To do this you will need to go to the customs office located at No. 6-8, Preah Norodom Blvd., Sangkat Phsar Thmei III, Khan Daun Penh, Phnom Penh, Cambodia.</p>\n<p>If you are importing/exporting any items deemed sensitive by the government then you will need to obtain a licence from the relevant ministry.</p>\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?q=customs%20office%2C%20phnom%20penh&amp;t=m&amp;z=14&amp;output=embed&amp;iwloc=near\" aria-label=\"customs office, phnom penh\"></iframe>', 'Export', '', 'inherit', 'closed', 'closed', '', '2048-revision-v1', '', '', '2019-07-26 18:42:50', '2019-07-26 11:42:50', '', '2048', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2048-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2050', '1', '2019-07-26 18:44:28', '2019-07-26 11:44:28', '<h2>ប្រព័ន្ធអាករអេឡិចត្រូនិច</h2>\nទិដ្ឋភាពសង្ខេបទូទៅនៃប្រព័ន្ធអាករអេឡិចត្រូនិចដែលត្រូវបានអនុវត្តថ្មី។\n\nប្រព័ន្ធ E-VAT គឺជាប្រព័ន្ធថ្មីមួយដែលត្រូវបានអនុវត្តនៅក្នុងប្រទេសកម្ពុជាក្នុងឆ្នាំ ២០១៩។ វាបង្កើតឡើងក្នុងគោលបំណងជួយរដ្ឋាភិបាលក្នុងការតាមដានការទូទាត់ កត់ត្រាការទទួលបង់ពន្ធឲ្យបានកាន់តែប្រសើរ។ ដើម្បីបញ្ចូលព័ត៌មានរបស់អ្នកទៅ ក្នុងប្រព័ន្ធអេឡិចត្រូនិចសូមចូលទៅកាន់គេហទំព័រ <a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">E-VAT។</a>\n<ul>\n 	<li style=\"font-weight: 400;\">អាជីវកម្មនីមួយៗនឹងត្រូវការបង្កើតឈ្មោះអ្នកប្រើ ហើយបំពេញព័ត៌មាន របស់​ក្រុមហ៊ុន។</li>\n 	<li style=\"font-weight: 400;\">រាល់អាជីវកម្មត្រូវការបញ្ចូលរាល់ការទិញ ដែលពួកគេត្រូវបានគេគិតពន្ធអាករ លើតម្លៃបន្ថែម។</li>\n 	<li style=\"font-weight: 400;\">រាល់អាជីវកម្មត្រូវការបញ្ចូលរាល់ការលក់ដែល ពួកគេបានលក់បានប្រចាំខែ។</li>\n</ul>\nកាលបរិច្ឆេទកំណត់តាមការប្រើប្រាស់ប្រព័ន្ធអេឡិចត្រូនិកគឺនៅរៀងរាល់ថ្ងៃទី ២០ នៃខែនីមួយៗ។ អ្នកត្រូវភ្ជាប់ឯកសារ E-VAT ទៅនឹងលិខិតប្រកាសពន្ធប្រចាំខែរបស់ អ្នក។', 'ប្រព័ន្ធអាករអេឡិចត្រូនិច', '', 'publish', 'closed', 'closed', '', '%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%96%e1%9f%90%e1%9e%93%e1%9f%92%e1%9e%92%e1%9e%82%e1%9f%92%e1%9e%9a%e1%9e%94%e1%9f%8b%e1%9e%82%e1%9f%92%e1%9e%9a%e1%9e%84%e1%9e%a2%e1%9e%b6%e1%9e%80%e1%9e%9a%e1%9e%9b', '', '', '2019-08-01 23:27:30', '2019-08-01 16:27:30', '', '0', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%96%e1%9f%90%e1%9e%93%e1%9f%92%e1%9e%92%e1%9e%82%e1%9f%92%e1%9e%9a%e1%9e%94%e1%9f%8b%e1%9e%82%e1%9f%92%e1%9e%9a%e1%9e%84%e1%9e%a2%e1%9e%b6%e1%9e%80%e1%9e%9a%e1%9e%9b/', '0', 'page', '', '0');
INSERT INTO `nge_posts` VALUES('2051', '1', '2019-07-26 18:44:29', '2019-07-26 11:44:29', '<h2>ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online</h2>		\n		<p>A brief overview of the newly implemented E-VAT system</p>\n		<p>ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ជាប្រព័ន្ធថ្មី១ដែលបានអនុវត្តក្នុងប្រទេសកម្ពុជាក្នុងឆ្នាំ២០១៩ ដើម្បីជួយរដ្ឋាភិបាលក្នុងការតាមដានការបង់ពន្ធតំលៃបន្តែមអោយមានលក្ខណះល្អប្រសើរ។ ដើម្បីបញ្ចូលព័ត៌មានរបស់អ្នកសូមចូលទៅកាន់គេហទំព័រខាងក្រោម។</p>\n<p><a href=\"https://owp.tax.gov.kh:50005/epaymentowpweb/login\">https://owp.tax.gov.kh:50005/epaymentowpweb/login</a></p>\n<ul>\n<li>អាជីវកម្មនីមួយៗនឹងត្រូវបង្កើតឈ្មោះអ្នកប្រើប្រាស់ ហើយបំពេញព័ត៌មានរបស់ក្រុមហ៊ុន។</li>\n<li>អាជីវកម្មនីមួយៗត្រូវតែបញ្ជូលការទិញដូរដែលពួកគេត្រូវបានគិតប្រាក់ថ្លៃពន្ធតំលៃបន្ថែម</li>\n<li>អាជីវកម្មទាំងអស់ត្រូវតែបញ្ចូលការលក់ទាំងអស់ដែលពួកគេបានធ្វើជារៀងរាល់ខែ</li>\n</ul>\n<p>កាលបរិច្ឆេតផុតកំណត់ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ៖ ថ្ងៃទី២០ រៀងរាល់ខែនីមួយៗ។</p>\n<p>អ្នកត្រូវតែភ្ជាប់ឯកសារប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online ទៅលើពន្ធប្រាក់ចំណេញប្រចាំខែរបស់អ្នក។</p>', 'ប្រព័ន្ធគ្រប់គ្រងអាករលើតម្លៃបន្ថែមតាម  Online', '', 'inherit', 'closed', 'closed', '', '2050-revision-v1', '', '', '2019-07-26 18:44:29', '2019-07-26 11:44:29', '', '2050', 'http://maskred.com/BIGkeenProject/BK_ngeayngeay/2019/07/26/2050-revision-v1/', '0', 'revision', '', '0');
