INSERT INTO `nge_posts` VALUES('2349', '1', '2019-08-01 10:25:58', '2019-08-01 03:25:58', '<h2>Monthly Tax &amp; NSSF Rates</h2>		\n		<p>Monthly Taxes are due at the 20th of each month, NSSF is due on the 15th of each month</p>\n		<p>ខាងក្រោមនេះអ្នកនឹងឃើញតារាងសង្ខេបនៃអត្រាពន្ធប្រចាំខែនិងអត្រាបេឡាជាតិរបបសន្តិសុខសង្គម.រាល់ពន្ធប្រចាំខែត្រូវដល់ថ្ងៃទី ២០ នៃខែនីមួយៗ  បេឡាជាតិរបបសន្តិសុខសង្គមត្រូវដល់ថ្ងៃទី ១៥ នៃខែនីមួយៗ។</p><p>ប្រសិនបើអ្នកចង់ប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នកសូមទៅរកអ្នកគណនាពន្ធរបស់យើងហើយការគណនាពន្ធនិងអនុញ្ញាតឱ្យវាប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នក </p>		\n			<h2>ពន្ធអាករនិងអប្បបរមា </h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធអាករនិងអប្បបរមា 						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធលើតម្លៃបន្ថែម				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធអប្បបរមា (មុនចំណេញ)				\n		</td>		<td data-title=\"អត្រា\">\n					1%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>ពន្ធលើប្រាក់ខែ</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធលើប្រាក់ខែ						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					0 - 1,200,000 KHR (0 - 300 USD) 				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					0%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					1,200,001 - 2,000,000 KHR (300 - 500)				\n		</td>		<td data-title=\"អត្រា\">\n					5%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					2,000,001 - 8,500,000 KHR (500 - 2,125 USD)				\n		</td>		<td data-title=\"អត្រា\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					8,500,001 - 12,500,000 KHR (2,125 - 3,125 USD)				\n		</td>		<td data-title=\"អត្រា\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					ច្រើនជាង12,500,001 KHR (ច្រើនជាង 3,125 USD)				\n		</td>		<td data-title=\"អត្រា\">\n					20%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					អាននិវាសនជន (អត្រាការប្រាក់)				\n		</td>		<td data-title=\"អត្រា\">\n					20%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>ពន្ធកាត់ទុក </h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធកាត់ទុក						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					ជួល				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					 សេវាកម្ម				\n		</td>		<td data-title=\"អត្រា\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					ការទូទាត់ការប្រាក់ (ដោយមិនរាប់បញ្ចូលការប្រាក់ដែលបង់ដោយធនាគារក្នុងស្រុក) 				\n		</td>		<td data-title=\"អត្រា\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					ថ្លៃឈ្នួល (ភាគលាភ)				\n		</td>		<td data-title=\"អត្រា\">\n					15%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					ការប្រាក់បានបង់លើការដាក់ប្រាក់មានកាលកំណត់ (ដោយធនាគារក្នុងស្រុក) 				\n		</td>		<td data-title=\"អត្រា\">\n					6%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធកាត់ទុក\">\n					ការប្រាក់បានបង់លើប្រាក់បញ្ញើមានកាលកំណត់មិនកំណត់ (ដោយធនាគារក្នុងស្រុក) 				\n		</td>		<td data-title=\"អត្រា\">\n					4%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>ពន្ធផ្សេងៗ </h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធផ្សេងៗ 						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធអត្ថប្រយោជន៍បន្ថែម				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					20%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធផ្សេងៗ \">\n					​ពន្ធបំភ្លឺសាធារណៈ				\n		</td>		<td data-title=\"អត្រា\">\n					3%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធស្នាក់នៅ				\n		</td>		<td data-title=\"អត្រា\">\n					2%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធផ្សេងៗ \">\n					ពន្ធលើការជួលអចលនទ្រព្យ				\n		</td>		<td data-title=\"អត្រា\">\n					10%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>អត្រាបេឡាជាតិរបបសន្តិសុខសង្គម</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)\">\n					គ្រោងការណ៍ហានិភ័យការងារ				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					0.8% នៃប្រាក់ឈ្នួល 				\n		</td></tr><tr>\n				<td data-title=\"បេឡាជាតិរបបសន្តិសុខសង្គម (ដល់ថ្ងៃទី ១៥)\">\n					គ្រោងការណ៍ថែរក្សាសុខភាព				\n		</td>		<td data-title=\"អត្រា\">\n					2.6% នៃប្រាក់ឈ្នួល 				\n		</td></tr>\n			</tbody>\n		</table>', 'Tax & NSSF Rates', '', 'inherit', 'closed', 'closed', '', '2038-revision-v1', '', '', '2019-08-01 10:25:58', '2019-08-01 03:25:58', '', '2038', 'http://ngeayngeay.co/2019/08/01/2038-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2350', '1', '2019-08-01 14:48:26', '2019-08-01 07:48:26', '<h2>ពន្ធប្រចាំខែ និងរបបសន្តិសុខសង្គម</h2>		\n		<p>ពន្ធប្រចាំខែគឺផុតកំណត់នៅថ្ងៃទី២០ជារៀងរាល់ខែ, របបសន្តិសុខសង្គមគឺផុតកំណត់នៅថ្ងៃទី១៥ជារៀងរាល់ខែ</p>		\n		<p>ខាងក្រោមនេះអ្នកនឹងឃើញតារាងសង្ខេបនៃអត្រាពន្ធប្រចាំខែនិងអត្រាបេឡាជាតិរបបសន្តិសុខសង្គម.រាល់ពន្ធប្រចាំខែត្រូវដល់ថ្ងៃទី ២០ នៃខែនីមួយៗ  បេឡាជាតិរបបសន្តិសុខសង្គមត្រូវដល់ថ្ងៃទី ១៥ នៃខែនីមួយៗ។</p><p>ប្រសិនបើអ្នកចង់ប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នកសូមទៅរកអ្នកគណនាពន្ធរបស់យើងហើយការគណនាពន្ធនិងអនុញ្ញាតឱ្យវាប៉ាន់ស្មានពន្ធប្រចាំខែរបស់អ្នក </p>		\n			<h2>ពន្ធអាករនិងអប្បបរមា </h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធអាករនិងអប្បបរមា 						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធលើតម្លៃបន្ថែម				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធអាករនិងអប្បបរមា \">\n					ពន្ធអប្បបរមា (មុនចំណេញ)				\n		</td>		<td data-title=\"អត្រា\">\n					1%				\n		</td></tr>\n			</tbody>\n		</table>\n			<h2>ពន្ធលើប្រាក់ខែ</h2>		\n		<table>\n						<colgroup>\n								<col span=\"1\">\n							</colgroup>\n		<thead>\n			<tr>\n			<th>\n							ពន្ធលើប្រាក់ខែ						\n				</th>\n			<th>\n							អត្រា						\n				</th>\n						</tr>\n		</thead><tbody><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					0 - 1,200,000 KHR (0 - 300 USD) 				\n		</td>		<td data-title=\"អត្រា\" colspan=\"2\">\n					0%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					1,200,001 - 2,000,000 KHR (300 - 500)				\n		</td>		<td data-title=\"អត្រា\">\n					5%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					2,000,001 - 8,500,000 KHR (500 - 2,125 USD)				\n		</td>		<td data-title=\"អត្រា\">\n					10%				\n		</td></tr><tr>\n				<td data-title=\"ពន្ធលើប្រាក់ខែ\">\n					8,500,001 - 12,500,000 KHR (2,125 - 3,125 USD)				\n		</td>		<