INSERT INTO `nge_posts` VALUES('2364', '1', '2019-08-01 22:53:29', '2019-08-01 15:53:29', '<h2>បញ្ជីត្រួតពិនិត្យការអនុវត្តន៍: ផ្នែកទី 3\n</h2>		\n		<p>អ្នកត្រូវដឹងពីល័ក្ខខណ្ឌគ្រប់យ៉ាងដែលមានស្របទៅតាមច្បាប់របស់កម្ពុជា។</p>		\n			<a href=\"http://ngeayngeay.co/kh/compliance-and-registration-checklist__trashed/\" role=\"button\">\n						Part 1: Registration\n					</a>\n			<a href=\"http://ngeayngeay.co/kh/compliance-and-registration-checklist-2__trashed/\" role=\"button\">\n						Part 2: Taxes\n					</a>\n			<a href=\"http://ngeayngeay.co/kh/updates/\" role=\"button\">\n						Updates\n					</a>\n			<h2>ផ្នែកទី 3: ច្បាប់​ការងារ</h2>		\n				<h3>\n					កុងត្រា\n				</h3>\n				<p>នេះគឺជាព័ត៌មានរបស់យើងសម្រាប់កិច្ចសន្យា។</p>\n			<a href=\"/BIGkeenProject/BK_ngeayngeay?page_id=998\" role=\"button\">\n						Read more here\n					</a>\n										<img width=\"1024\" height=\"768\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-1024x768.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-1024x768.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-300x225.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-768x576.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-463x348.jpg 463w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-640x480.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-400x300.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-367x275.jpg 367w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-200x150.jpg 200w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" />											\n				<h3>\n					ថ្ងៃឈប់សម្រាក់\n				</h3>\n				<p>មានថ្ងៃឈប់សម្រាកមួយចំនួនដែលបុគ្គលិកមានសិទ្ធឈប់សម្រាកនៅក្នុងប្រទេស\nកម្ពុជា។\n</p>\n			<a href=\"/BIGkeenProject/BK_ngeayngeay?page_id=998\" role=\"button\">\n						Read more here\n					</a>\n										<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" />											\n				<h3>\n					ប្រាក់ខែ និងសំណងនានា\n				</h3>\n				<p>គ្រប់យ៉ាងដែលអ្នកត្រូវដឹងពីប្រាក់ខែ និងសំណងនានានៅក្នុងប្រទេសកម្ពុជា</p>\n			<a href=\"http://ngeayngeay.co/kh/%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%94%e1%9f%80%e1%9e%9c%e1%9e%8f%e1%9f%92%e1%9e%9f%e1%9e%9a%e1%9f%8d-%e1%9e%93%e1%9e%b7%e1%9e%84-%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80/\" role=\"button\">\n						Read more here\n					</a>\n										<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" />											\n				<h3>\n					ការបង់ប្រាក់អតីតភាពការងារ\n				</h3>\n				<p>ព័ត៌មានច្បាស់លាស់សម្រាប់។ការបង់ប្រាក់អតីតភាពការងារ</p>\n			<a href=\"http://ngeayngeay.co/kh/%e1%9e%80%e1%9e%b6%e1%9e%9a%e1%9e%94%e1%9e%84%e1%9f%8b%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%a2%e1%9e%8f%e1%9e%b8%e1%9e%8f%e1%9e%97%e1%9e%b6%e1%9e%96/\" role=\"button\">\n						Read more here\n					</a>\n										<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" />											\n			<h2>ត្រូវការ​ជំនួយ</h2>		\n			<a href=\"http://ngeayngeay.co/kh/%e1%9e%91%e1%9f%86%e1%9e%93%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%91%e1%9e%84%e1%9e%99%e1%9e%be%e1%9e%84/\" role=\"button\">\n						Contact Us\n					</a>', 'Compliance Checklist: Part 3', '', 'inherit', 'closed', 'closed', '', '2068-revision-v1', '', '', '2019-08-01 22:53:29', '2019-08-01 15:53:29', '', '2068', 'http://ngeayngeay.co/2019/08/01/2068-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `nge_posts` VALUES('2365', '1', '2019-08-01 22:55:34', '2019-08-01 15:55:34', '<h2>បញ្ជីត្រួតពិនិត្យការអនុវត្តន៍: ផ្នែកទី 3</h2>\nអ្នកត្រូវដឹងពីល័ក្ខខណ្ឌគ្រប់យ៉ាងដែលមានស្របទៅតាមច្បាប់របស់កម្ពុជា។\n\n<a href=\"http://ngeayngeay.co/kh/compliance-and-registration-checklist__trashed/\" role=\"button\">\nPart 1: Registration\n</a>\n<a href=\"http://ngeayngeay.co/kh/compliance-and-registration-checklist-2__trashed/\" role=\"button\">\nPart 2: Taxes\n</a>\n<a href=\"http://ngeayngeay.co/kh/updates/\" role=\"button\">\nUpdates\n</a>\n<h2>ផ្នែកទី 3: ច្បាប់​ការងារ</h2>\n<h3>\n					កុងត្រា</h3>\nនេះគឺជាព័ត៌មានរបស់យើងសម្រាប់កិច្ចសន្យា។\n\n<a href=\"/BIGkeenProject/BK_ngeayngeay?page_id=998\" role=\"button\">\nRead more here\n</a>\n<img width=\"1024\" height=\"768\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-1024x768.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-1024x768.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-300x225.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-768x576.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-463x348.jpg 463w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-640x480.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-400x300.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-367x275.jpg 367w, http://ngeayngeay.co/wp-content/uploads/2019/06/AGAPE-Coffee-200x150.jpg 200w\" sizes=\"(max-width: 1024px) 100vw, 1024px\">\n<h3>\n					ថ្ងៃឈប់សម្រាក់</h3>\nមានថ្ងៃឈប់សម្រាកមួយចំនួនដែលបុគ្គលិកមានសិទ្ធឈប់សម្រាកនៅក្នុងប្រទេស\nកម្ពុជា។\n\n<a href=\"/BIGkeenProject/BK_ngeayngeay?page_id=998\" role=\"button\">\nRead more here\n</a>\n<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0188-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\">\n<h3>\n					ប្រាក់ខែ និងសំណងនានា</h3>\nគ្រប់យ៉ាងដែលអ្នកត្រូវដឹងពីប្រាក់ខែ និងសំណងនានានៅក្នុងប្រទេសកម្ពុជា\n\n<a href=\"http://ngeayngeay.co/kh/%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%94%e1%9f%80%e1%9e%9c%e1%9e%8f%e1%9f%92%e1%9e%9f%e1%9e%9a%e1%9f%8d-%e1%9e%93%e1%9e%b7%e1%9e%84-%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80/\" role=\"button\">\nRead more here\n</a>\n<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0335-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\">\n<h3>\n					ការបង់ប្រាក់អតីតភាពការងារ</h3>\nព័ត៌មានច្បាស់លាស់សម្រាប់។ការបង់ប្រាក់អតីតភាពការងារ\n\n<a href=\"http://ngeayngeay.co/kh/%e1%9e%80%e1%9e%b6%e1%9e%9a%e1%9e%94%e1%9e%84%e1%9f%8b%e1%9e%94%e1%9f%92%e1%9e%9a%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%a2%e1%9e%8f%e1%9e%b8%e1%9e%8f%e1%9e%97%e1%9e%b6%e1%9e%96/\" role=\"button\">\nRead more here\n</a>\n<img width=\"1024\" height=\"683\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-1024x683.jpg\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-1024x683.jpg 1024w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-300x200.jpg 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-768x512.jpg 768w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-640x427.jpg 640w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-400x267.jpg 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/DSCF0472-367x245.jpg 367w\" sizes=\"(max-width: 1024px) 100vw, 1024px\">\n<h2>ត្រូវការ​ជំនួយ</h2>\n<a href=\"http://ngeayngeay.co/kh/%e1%9e%91%e1%9f%86%e1%9e%93%e1%9e%b6%e1%9e%80%e1%9f%8b%e1%9e%91%e1%9e%84%e1%9e%99%e1%9e%be%e1%9e%84/\" role=\"button\">\nContact Us\n</a>', 'បញ្ជីត្រួតពិនិត្យការអនុវត្តន៍: ផ្នែកទី 3', '', 'inherit', 'closed', 'closed', '', '2068-revision-v1', '', '', '2019-08-01 22:55:34', '2019-08-01 15:55:34', '', '2068', 'http://ngeayngeay.co/2019/08/01/2068-revision-v1/', '0', 'revision', '', '0');
