INSERT INTO `nge_posts` VALUES('2421', '1', '2019-08-02 08:10:06', '2019-08-02 01:10:06', '<h2>Registration Summary</h2>		\n		<p>A step by step summary of the entire registration process</p>		\n			<figure><a href=\"http://ngeayngeay.co/documents/\"><img width=\"128\" height=\"128\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399656_Branding.png\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399656_Branding.png 128w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399656_Branding-85x85.png 85w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399656_Branding-50x50.png 50w\" sizes=\"(max-width: 128px) 100vw, 128px\" /></a></figure><h3><a href=\"http://ngeayngeay.co/documents/\">1. Collect all the required documents</a></h3>		\n			<figure><a href=\"http://ngeayngeay.co/register-moc/\"><img width=\"512\" height=\"512\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design.png\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design.png 512w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-150x150.png 150w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-300x300.png 300w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-400x400.png 400w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-367x367.png 367w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-85x85.png 85w, http://ngeayngeay.co/wp-content/uploads/2019/06/1470399594_Web_Design-50x50.png 50w\" sizes=\"(max-width: 512px) 100vw, 512px\" /></a></figure><h3><a href=\"http://ngeayngeay.co/register-moc/\">2. Register with the Ministry of Commerce</a></h3>		\n			<figure><a href=\"http://ngeayngeay.co/register-gdt/\"><img width=\"128\" height=\"128\" src=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399662_Marketing.png\" alt=\"\" srcset=\"http://ngeayngeay.co/wp-content/uploads/2019/06/1470399662_Marketing.pn